style.css:
@-moz-document url-prefix("http://play.typeracer.com/"), domain("play.typeracer.com") {


/* hide advertisement */
.newNorthWidget, .newSouthWidget, .newEastWidget, .newWestWidget, .adblockWarning {
    display: none !important;
}

body { /* define background and text color */
  color: #A4A4A4;
  background-color: #232323;
  background-image: url(data:image/png;base64,);
  background-repeat: repeat-x;
}

h1, h2, h3 { color: #A4A4A4; }
a:hover { color: #6AB5FF; } /* fits better to the icon's */
.exceptionNoticePopup a:hover { color: #6AB5FF; }
.exceptionNoticePopup a { color: #FF7700; }
.navigation a:hover { color: #6AB5FF; background-color: #313131; } /* fits better to bg.cache image */

.container { /* new typeracer logo */
  background-image: url(data:image/png;base64,);
  background-repeat: no-repeat;
}

.main { /* remove bgmain.cache.png (left side) */
  background-color: #232323;
  background-image: none;
}

.DialogBox { /* login */
  background-color: #2F2F2F;
  border-radius: 15px;
  padding: 15px;
}

.signUpForm .sectionHeaderRow { /* account information */
  color: #6AB5FF;
}

.playerInfoPopup { /* racer card */
  background-color: #2F2F2F; color: #C8C8C8;
  box-shadow: 5px 5px rgba(0,0,0,0.5); /* shadow with opacity of 50% */
  border-radius: 15px;
  padding: 15px;
}

/* =========
   racetrack
   ========= */

.mainViewport {
  background-color: #2A2A2A;
  border-radius: 15px;
  padding: 15px;
  border-width: 1px;
  border-style: solid;
  border-color: #803C00; /*#4A4A4A;*/
}

.chrome_l,
.chrome_m,
.chrome_r,
.chrome_t,
.chrome_tr,
.chrome_tl,
.chrome_b,
.chrome_bl,
.chrome_br {
  background: #2A2A2A;
}

.gameView .gameStatusLabel { color: #A4A4A4; }
.textInfoView .textInfoHeader { color: #A4A4A4; }
/* text title and race indices numbers */
.textInfoView .tblOwnStats { color: #A4A4A4; }
.textInfoView .tblOwnStatsNumber { color: #A4A4A4; }
.textInfoView .textInfoTitle { color: #DAA520 !important; }
.textInfoView .textInfoTitle a { color: #DAA520 !important; }
/* good or bad accuracy.. */
.textInfoView .tblOwnStatsNumber-good{color:#99CC00;}
.textInfoView .tblOwnStatsNumber-bad{color:#D08383;}
/* countown.. */
.countdownPopup { background-color: #2B4866; }
/* text to type - (*) overwrite font-family for the childs */
.gameView .inputPanel * { font-family: "Courier New"; font-weight: normal; font-size: 20px; }
.gameView .inputPanel .txtInput { color: #A0A0A0; border-width: thin; }
.gameView .inputPanel .txtInput-error { color: #FFFFFF; }
/* help arrows (only at begin visible) */
.arrowPopupText { color: #0000A0; }
/* rent a porsche */
.avatar img {
  background-image: url(data:image/png;base64,);
  background-repeat: no-repeat;
}
/* not perfect, hide standard avatar then premium avatar is present */
.gameView .gwt-Image {
    background-image: none;
}

.statsView { /* top 20 list - add round edges */
  background-color: #2A2A2A;
  border-radius: 15px;
  padding: 15px;
  border-width: 1px;
  border-style: solid;
  border-color: #803C00; /*#4A4A4A;*/
}

.footer { /* recolor the blue footer */
  background-color: #6B6B6B;
  background-image: url(data:image/png;base64,);
  background-position: 0 -92px;
  background-repeat: repeat-x;
  margin-top: 12px;
}



}
