style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.toodledo.com") {

 
  #main, #left_side, #left_side div
  {
    border-color: #FFF !important;
    background-image: none !important;
    background-color: #FFF !important;
  }

  /* --- 1ST TOOLBAR --- */
  #rchunktop, #rchunktop div
  {
    border-bottom-color: #173C60 !important;
  }

 /* "add task" button */

  #rchunktop div#tlbr_new
  {
    background-image: none !important;
    background-color: #173C60 !important;
    color: #EEE !important;
    border-color: #173C60 !important;
    text-decoration: underline !important;
  }

  #rchunktop > div#head > div#tlbr_new a
  {
    color: #EEE;
  }

  #rchunktop > div #head > div#tlbr_new:hover
  {
    background-image: none !important;
    background-color: #173C60 !important;
    color: #FFF !important;
  }

  /* one too many white borders... */
  #rchunktop div#tlbr_quick
  {
    border-left-color: #173C60 !important;
  }

  /* --- 2ND TOOLBAR --- */
  #rchunk > div#colhead > div, #rchunk > div#colhead, #rchunk > div#colhead a
  {
    color: #EEE !important;
    background-color: #173C60 !important;
    border-color: #173C60 !important;
  }

  div#colhead
  {
    margin-left:1px !important;
  }

  #rchunk #tb_cust
  {
    background-image: none !important;
    background-color: inherit !important;
  }

  /* --- SEPARATORS --- */
  #rchunk > div#tasks > div.sep
  {
    background-color: #D0D0EE !important;
    border-color: #D0D0EE !important;
  }
}
