style.css:
@-moz-document domain(twitter.com) {

span.tweet-user-name:before {
  content:'Tweet';
  font-weight:bold;
  text-indent:-9999px;
}
span.tweet-user-name>a{ display:none; }

div.tweet-image
{
  border-radius:5px;
  background-image:
   url(http://a1.twimg.com/sticky/default_profile_images/default_profile_6_normal.png);
  text-indent:-9999px;
}

div.tweet-image>img { display:none; }

}
