style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com'), url-prefix('https://www.tumblr.com')   {

div.l-header .tab .tab_notice {
background: #000000;
}


ol#posts li.notification {

  opacity: .9;

  background-color: #ae8d7e!important;

  border-top: 1px dashed #ae8d7e !important;

  border-left: 1px solid #ae8d7e !important;

  border-bottom: 1px dashed #ae8d7e !important;

  border-right: 1px solid #ae8d7e !important;

  color: #2c2020 !important;

}

ol#posts li.notification.last_notification {
    border-bottom: 0px solid #000000;
}

.answer_container {

  background-color: #af8e78 !important;

}


body {

background: #efcaba url('http://i.imgur.com/QRllA.png') !important;
background-position: right bottom !important;
background-attachment:fixed !important;
background-repeat:no-repeat !important;

}


#posts .notification a:not(.avatar_frame) {

color: rgba(75, 60, 60, 1) !important;

font-weight: bold !important;

border-bottom: 0px solid #ffffff;

-webkit-background-clip: padding-box;

}



#posts .notification blockquote {

  border-left: solid 3px #a10000 !important;

}


#posts .notification .avatar_frame {

  opacity: .9;

  background-color: #ffffff !important;

  border: 2px solid #b98078 !important;

  position: absolute;

  top: -1px;

  left: -55px;

  overflow: hidden;

  display: inline-block;

}


.post.is_mine.with_avatar.new_post {

  color: #ffffff !important;

  font-family:helvetica, monospace !important;

  text-decoration: none;

  text-transform: none;


  background: rgba(0,0,0,0.50) !important; 

}


img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{

opacity:0.00 !important;  

}


#new_post{

  background-image: url(http://assets.tumblr.com/images/new_post_icons_no_labels.png) !important; 

  background-repeat: no-repeat !important; 

}


#posts .post.new_post .new_post_label {

-webkit-font-smoothing: antialiased;

opacity: .9;

font-size: 16px;

font-weight: normal;

color: #ffffff;

text-decoration: none;

text-transform: none;

text-align: center;

width: 56px;

position: absolute;

}



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

  background: rgba(161, 90, 90, .5) !important; 

}


.selection_nipple:not([class="selection_nipple white"]) {

border-bottom: solid 11px rgba(00,00,00, 0.50)  !important;

}



#content_top {

  width: 0px !important;

  height: 0px !important;

}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

background: none !important;

width: 0px !important;

height: 0px !important;

margin-top: 0px !important;

border-bottom: solid 11px rgba(00,00,00, 0.50) !important;

border-right: 11px solid transparent !important;

border-left: 11px solid transparent !important;

}



#content_bottom {

  width: 0px !important;

  height: 0px !important;

}



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

  padding-top: 25px !important; 

  padding-bottom: 25px !important; 

   -moz-border-radius: 20px !important; 

}


LABEL {

  color: rgba(0,0,0, 1.0) !important; 

}




#footer {

  display: none !important; 

}


.radar {

  display: none !important; 

}


.selected .following,

.selected .followers,

.selected .settings,

.selected .posts,

.selected .messages,

.selected .drafts,

.selected .queue,

.selected .likes  {

  color: rgba(0, 0, 0, 0.50) !important; 

  background: rgba(0, 0, 0, 0.0) !important; 

}


#new_post .arrow {

  opacity: 0.50 !important;

  margin-left: -1px !important; 

}

}
