style.css:
@-moz-document domain("joesgoals.com") {
body{
    font-color: #111 !important;
    font-size: 11px !important;
    font-family: verdana,helvetica,arial,sans-serif !important;
    vertical-align: baseline !important;
    font-weight: lighter !important;
}
a, .active_control, a > .control, span > .control{
    text-decoration: none !important;
    color: #297B94 !important;
}
.dayOff > * {
    background-color: #DEDEDE !important;
}
body, .jcornerfg, #trackerTable .activeDay{
    background-color: white !important;
}
#details, #headJG, #caption, .jcorner, .goalImage, #tagBox form, hr{ 
    display: none !important;
}
#trackerTable td {
    border: none;
}
#trackerTable tr .goalTitle {
    text-align: left !important;
    padding-right: 2px !important;
    height: auto !important;
    width: auto !important;
}
#controls{
    border-bottom: 2px black solid !important;
}
#controlLinks{
    letter-spacing: 1.5px !important;
}
#trackerTable .dayHead, #trackerTable .dayHead .weekEnd{
    font-weight: normal !important;
    color: #5C9FAD !important;
}
#trackerTable {
    width: 600px !important;
}
#trackerTable .dayOff .trackBox, #trackerTable .dayOn .trackBox{
    height: 30px;
    width: 30px;
}
}
