style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('tppcrpg.net'), url-prefix('http://tppcrpg.net') {
body{
	background-color: #000000 !important;
}
.notice, #cBox {border: 3px solid white !important;}
#header {
	background-image:url('http://i53.tinypic.com/2pzbc7m.jpg') !important;
	background-color: #000000 !important;
}
#configure li hr,#tAoDp,#tAoDp2 {
	display:none !important;
}
h2, h3,.Trainer1, .Trainer2,.not,#cBox p.center a,#towers td a {
	color:white !important;
}
.innerContent {
background-color: black !important;
}
.rosterContent .row0, .rosterContent .row1 {background-color: black !important;color:white !important;}
.rosterContent a {color:white !important;text-decoration:none !important;font-weight:bold !important;}
legend,#profile li div.h,.item{color:white !important;}
li.h,#switchSidebar div {color:black !important;background-color:white !important;}
#cancelBattle a{text-decoration:none !important;color:white !important;font-weight:bold !important;}
#rightR ul li div.sl ul li a,#inner{color:white !important;}
#stats .statsR a{text-decoration:underline !important;color:white !important;font-weight:bold !important;}
#leftR h3, #rightR h3, #leftR ul li div, #rightR ul li div {
background-color: black !important;
border: 0px  !important;
}
#inner a {color:white !important;text-decoration:none !important;font-weight:bold !important;}
tr,#breed .holder .starter, #breed ul li div.roster,.eBox div,#configure li select, #configure li input,.dex .iBox div,#towers td div.i,.maintenance,.ranks th,.notice, #cBox ,.dex div,.dex tr th,.dex td table td,#inner .r1,#inner .r0,#profile li div.i,#configure li p{background-color:black !important;color:white !important;}
.success,.error,select{
background-color: #000000 !important;
border: 3px solid #fff !important;
}
.profileBox,#towers th {
border: 1px solid #fff !important;
background-color: #000 !important;
}
.ld,input.center {
border: 1px solid #fff !important;
color:black !important;
}
#buy td {
background-color: black !important;
border: 1px solid #CCC !important;
}
#rightR ul li div.sl ul li a:hover{
color:black !important;
background-color:white !important;
text-decoration:none !important;
}
tr.row0,tr.row1{color:white !important;}
#footer_clear{background-color:black !important;}
div#footer p{background-color: #EBE7DB !important;color:black !important;}
div#left a,div#right a{color:black !important;text-decoration:none !important;}
}
