style.css:
@-moz-document url-prefix('http://www.tumblr.com'), url-prefix('https://www.tumblr.com')   {



ol#posts li.notification {



  background-color: #3749B4 !important;



  border-top: 1px solid #3749B4!important;



  border-left: 1px solid #3749B4!important;



  border-bottom: 1px solid #3749B4!important;



  border-right: 1px solid #3749B4!important;



  color: #ffff00 !important;



  text-decoration: none;



}







body {



background-color: #00004b;

background-image: url('http://i155.photobucket.com/albums/s317/Jamcub/tumblr_lonhvlq3Fo1qboi42o1_500-1.png');

background-position: right bottom;

background-attachment:fixed;

background-repeat:no-repeat;



}



#posts .notification a:not(.avatar_frame) {



color: rgba(0, 55, 255, 1);



font-weight: bold;



text-decoration: none;



border-bottom: 0px solid #ffffff;



-webkit-background-clip: padding-box;



}







#posts .notification blockquote {



  border-left: solid 3px #005682;



}







#posts .notification .avatar_frame {



background-color: #ffffff;



border: 1px solid #000000;



position: absolute;



top: -1px;



left: -55px;



overflow: hidden;



display: inline-block;



}













.post.is_mine.with_avatar.new_post {



  color: #ffffff;



  font-family:courier, monospace;



  background: rgba(0,0,0,0.50) !important; 



}









img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{



opacity:0.00 !important;  



}





#new_post{



  background-image: url(http://assets.tumblr.com/images/new_post_icons_no_labels.png) !important; 



  background-repeat: no-repeat !important; 



}





#posts .post.new_post .new_post_label {



-webkit-font-smoothing: antialiased;



opacity: .9;



font-size: 14px;



font-weight: normal;



color: #ffffff;



text-decoration: none;



text-transform: uppercase;



text-align: center;



width: 74px;



position: absolute;



}







#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



  background: rgba(88, 88, 200, .5) !important; 



}















.selection_nipple:not([class="selection_nipple white"]) {



border-bottom: solid 11px rgba(00,00,00, 0.50)  !important;



}







#content_top {



  width: 0px !important;



  height: 0px !important;









}



.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {



background: none !important;



width: 0px !important;



height: 0px !important;



margin-top: 0px !important;



border-bottom: solid 11px rgba(00,00,00, 0.50) !important;



border-right: 11px solid transparent !important;



border-left: 11px solid transparent !important;



}









#content_bottom {



  width: 0px !important;



  height: 0px !important;



















}











#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



  padding-top: 25px !important; 



  padding-bottom: 25px !important; 



   -moz-border-radius: 20px !important; 



}











LABEL {



  color: rgba(0,0,0, 1.0) !important; 



}









#footer {



  display: none !important; 



}







.radar {



  display: none !important; 



}





.selected .following,



.selected .followers,



.selected .settings,



.selected .posts,



.selected .messages,



.selected .drafts,



.selected .queue,



.selected .likes  {



  color: rgba(0, 0, 0, 0.50) !important; 



  background: rgba(0, 0, 0, 0.0) !important; 



}











#new_post .arrow {



  opacity: 0.50 !important;



  margin-left: -1px !important; 



}





}
