style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(gendou.com),
domain(animesheep.com) {
body {
    max-width:1224px !important;
    margin:auto;
    background:-moz-linear-gradient(left, #151515, #001515, #151515)!important;
}

#navPanel{
    background-color:#000000!important;
    -moz-border-radius:10px!important;
    opacity:.9!important;
    padding:0 10px 0 10px!important;
}
.navHorizontal li{
    background:transparent !important;
}

.navVertical{
    background-color:#000000!important;
    -moz-border-radius:0 0 10px 10px!important;
}

div:nth-child(12), div:nth-child(6), div:nth-child(5), div:nth-child(3){
    margin:-10px auto -10px auto!important;
    padding:25px 0 50px 0;
    background-color:#000!important;
    -moz-border-radius:10px!important;
    border:#fff 1px solid;
}

div:nth-child(11){
    margin:-24px auto -10px auto!important;
    padding:25px 0 50px 0;
    background-color:#000!important;
    -moz-border-radius:10px!important;
    border:#fff 1px solid;
}


table, td, th, th.dead{
    background-color:#100!important;
    border:#222222 0px solid!important;
    -moz-border-radius:15px!important;
}

td{
    border:#020000 1px solid!important;
    padding:0 0 0 25px!important;
}
table{
    width:95%;
    margin:10px auto 30px auto !important;
}

td .body {
    background-color:#555!important;
}

.center{
    max-width:95%;
    margin:-27px auto 0 auto;
    padding:10px 0 0 0;
    background-color:#000!important;
    -moz-border-radius:10px!important;
}

div .news {
    width:95%;
    margin:-7px auto 7px auto!important;
    padding: 7px !important;
    background-color:#100!important;
    -moz-border-radius:10px!important;
    border:#fff 1px solid;
}
hr{display:none}

}
