style.css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Japanese */
@-moz-document domain(techon.nikkeibp.co.jp) {
/* Top & Article */
  /* ads */
    div#superBunner, div.rectangle, div#rectangle,

/* Article */
  /* login_alert */
    div#nocookie,
  /* ads */
    div#keywordad, div#textad,
  /* member_tools */
    div#relatedLink, div#feedback

  { display:none !important; }
}

/* English */
@-moz-document url-prefix("http://techon.nikkeibp.co.jp/english/") {
  div#rotation, div#ban300
  { display:none !important; }
}

/* Chinese */
@-moz-document domain(china.nikkeibp.com.cn), domain(big5.nikkeibp.com.cn) {
  /* ads */
    div#rightad_a, div#topad, div.ad2-in, div.rightad2, div.rjbp,
    div.viewads,
  /* article_header */
    div.thebox,
  /* login_alert */
    div#coverDiv,
  /* member_tool */
    div#pinlun,
  /* article_footer */
    div.textad
  { display:none !important; }
}
