style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("community.cloudchaser.us") {

/* Normalize **********************************************************************/
/**********************************************************************************/
.tborder[cellspacing="1"] { border-collapse:separate !important; }
.tborder[style="border-top-width: 0;"], .tborder[id^="post"] { border-collapse:collapse !important; }

/* ______________________________________________________________________________ */
/* ############################# STYLE OPTION ################################### */
/*                                                                                */

/* Layout-Type ********************************************************************/
/**********************************************************************************/
/* Fixed by default */
#container { margin:20px auto !important; }
#menu_container { width:950px !important; padding:0 !important; }

/* Override to widescreen if selected on install */
/*[[layout-type]]*/
#container, #games_background, #menu_container { width:auto !important; }
#container { margin:20px !important; }
/* */

/* Body Styling *******************************************************************/
/**********************************************************************************/
html, body { padding:0 !important; margin:0 !important; }
html { border-bottom:4px solid #FF9000; }
body { }

input[class="button"]:hover { cursor:pointer !important; }

#container {
background: #141414 !important;
color:#fff !important;
  -webkit-box-shadow: 0px 0px 20px rgba(0,0,0,0.2); 
     -moz-box-shadow: 0px 0px 20px rgba(0,0,0,0.2);  
          box-shadow: 0px 0px 20px rgba(0,0,0,0.2); }

body { background:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAAAAACMmsGiAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAABdJREFUCB1jUlGRkWH6CQRMf//y8oJZAFUaCmUwcfODAAAAAElFTkSuQmCC) repeat 0 0 #000 !important; }

/* Font Color */
* { color:#fff !important; }
#header { color: #fff !important; }

/* Links */
a { color:#3F89FF !important; }
a:hover { color:#FF9000 !important; }

#games_background { margin:0 20px !important; background-color:#413D32 !important; display:block !important; }


/* Page Header ******************************************/


/* Navigation */
#menu_container div#pagemenucontainer ul#pagemenu li a:hover { color:#fff !important; } 

/* !end Navigation */


/* #header_bar */
/* Possible the advertisement bar? Let's reset the height so it doesn't appear unless it has content. */
#header_bar { background:rgba(0,0,0,0.5) !important; margin-top:20px !important; }
#header_bar, #header_bar * { height:auto !important; padding:0 !important; }



/* Welcome Back Bar #panel */
/* Default Toolbar Panel Styling */
#panel { color: #fff !important; }

/* ______________________________________________________________________________ */
/* ############################# STYLE OPTION ################################### */
/*                                                                                */
/* Inline Toolbar Panel */ /* ON */
#panel { background-color:#0C0C0C !important; border-color:#232220 !important;  
  -webkit-border-radius: 4px; -moz-border-radius: 4px; border-radius: 4px; }

/* Fixed Toolbar Panel */ /* OFF */
/*[[fixed-toolbar]]*/
#panel { background:rgba(0,0,0,0.75) !important; font-size:75% !important; position:fixed; top:0; left:0; width:100% !important; border:0 !important; border-bottom:1px solid #000 !important; z-index:9999; -webkit-border-radius: 0; -moz-border-radius: 0; border-radius: 0; 
  -webkit-box-shadow: 0px 0px 8px #000; -moz-box-shadow: 0px 0px 8px #000; box-shadow: 0px 0px 8px #000;
  -webkit-box-sizing: border-box; -moz-box-sizing: border-box; box-sizing: border-box; }
#panel:hover { background:rgba(0,0,0,1) !important; }
hr + br[class="clear"] { display:none !important; }
body { padding-top: 40px !important; }
/* */
/*[[color-theme]]*/
#panel * { color:#fff !important; }
#panel { background-color:rgba(63,137,255,0.95) !important; border:0 !important; -webkit-box-shadow: 0px 0px 4px rgba(0,0,0,0.5); -moz-box-shadow: 0px 0px 4px rgba(0,0,0,0.5); box-shadow: 0px 0px 4px rgba(0,0,0,0.5); }
#panel a, #panel a:visited { color:#0a357a !important; }
#panel a:hover { color:#FF9000 !important; }
#panel:hover { background-color:#337bef !important; }
/* */

/* !end Page Header */


/* Posts ****************************/

/* Pagination */
.pagination_current { background-color:rgba(0,0,0,0.25) !important; }
.pagination_page { }
.pagination_next { }
.pagination a:hover { background-color:#3F89FF !important; }
.pagination a:hover { color:#fff !important; text-decoration:none !important; }
.pagination { padding-bottom:10px !important; }


/* Post Title */
.float_right + .tborder { padding: 0 !important; }
.float_right + .tborder, .float_right + .tborder .thead[colspan="2"] { background:transparent !important; border:0 !important;  }

.float_right + .tborder .thead[colspan="2"] { 
border-top:1px solid #FF9000 !important; 
border-left:1px solid #FF9000 !important; 
border-right:1px solid #FF9000 !important; 
border-bottom:1px solid #FF9000 !important; 

background: #0e1a2d !Important; /* Old browsers */
background: -moz-linear-gradient(top, #0e1a2d 0%, #0e1a2d 41%, #08111e 100%) !Important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#0e1a2d), color-stop(41%,#0e1a2d), color-stop(100%,#08111e)); /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, #0e1a2d 0%,#0e1a2d 41%,#08111e 100%); /* Chrome10+,Safari5.1+ */
background: linear-gradient(top, #0e1a2d 0%,#0e1a2d 41%,#08111e 100%) !Important; /* W3C */
 }


.float_right + .tborder .thead[colspan="2"] { padding:30px !important; }

div.float_right + .tborder .thead[colspan="2"] > div:last-child > strong { font-family: 'Droid Sans' !important; color:#FF9000 !important; font-weight:700 !important; font-size:26px !important; line-height:26px !important; text-transform: none !important; text-shadow:1px 1px 2px #000; }



/* Post Table */
/* First Post */



table.tborder[id^="post"] {
overflow:hidden !important; border:0px !important;

  -webkit-box-shadow: 0px 5px 10px rgba(0,0,0,0.5); 
     -moz-box-shadow: 0px 5px 10px rgba(0,0,0,0.5); 
          box-shadow: 0px 5px 10px rgba(0,0,0,0.5); 
 }

/* Avatar Boxes ****************************************************************/
#posts .tborder[id^="post"] td[class^="trow"] { min-width:150px !important; max-width:150px !important; overflow:hidden !important; }

#posts .tborder[id^="post"] tbody tr td[class^="trow"] br + .smalltext { white-space:pre-line; word-wrap: break-word !important; }

  /* Posts/Joined/Rep */
  #posts .tborder[id^="post"] td[class^="trow"] br + .smalltext span:last-child { color:#494949 !important; }
  #posts .tborder[id^="post"] td[class^="trow"] br + .smalltext span a strong { color:#898989 !important; }
  #posts .tborder[id^="post"] td[class^="trow"] br + .smalltext span a:hover strong { color:#FF9000 !important; }
  #posts .tborder[id^="post"] td[class^="trow"] br + .smalltext span a:hover { text-decoration:none !important; }

/* ______________________________________________________________________________ */
/* ############################# STYLE OPTION ################################### */
/*                                                                                */
/* Remove the posters details */
/* ON by default */
/*[[poster-details]]*/
#posts .tborder[id^="post"] td[class^="trow"] br + .smalltext span:last-child { display:none; }
/* */
	
  /* Online Indicator */
  img[title$="line"], img[alt$="line"] {   }
  img[alt$="Online"] { width:8px; height:8px; } /* Online */
  img[alt$="Offline"] { display:none; } /* Offline */

  /* Post Time */
  #posts .tborder td[class^="trow"] .smalltext:first-child { color:#494949!important; font-size:8px !important; }


/* Post Content */
/* Remove the bottom padding from the Avatar box and Post Content box. */
#posts .tborder[id^="post"] tr:first-child td[class^="trow"] { padding-bottom:0 !important; }


/* Dividers */
#posts .tborder hr { background: #000 !important; margin-top:20px !important; }



/* Background Colors */
#posts > table:nth-child(even), #posts > table:nth-child(even) tr, #posts > table:nth-child(even) td { background-color:#282828 !important; }
.tborder, .tborder tr, .tborder td                       { background-color: #1E1E1E !important; }
#posts .tborder [class^="trow"]:first-child              { background: rgba(0,0,0,0.15) !important; }
#posts .tborder tbody tr td[class^="trow"]:first-child * { background:none !important; }




div#container div#content div#posts table.tborder { margin-top:15px !important; padding:0 !important; }

#container div#content div#posts table.tborder:first-child { margin-top:0 !important; }


/* Images in Post Footers */

#posts td[class^="trow"] td[align$="t"] a img { height:15px !important; opacity:0;
  -webkit-transition: all 0.3s ease-out 0s;
     -moz-transition: all 0.3s ease-out 0s;
      -ms-transition: all 0.3s ease-out 0s;  
       -o-transition: all 0.3s ease-out 0s;  
          transition: all 0.3s ease-out 0s;  
 }

#posts table:hover td[class^="trow"] td[align$="t"] a img { opacity:.5;

  -webkit-transition: all 1s ease-out .25s; 
     -moz-transition: all 1s ease-out .25s;  
      -ms-transition: all 1s ease-out .25s;  
       -o-transition: all 1s ease-out .25s;  
          transition: all 1s ease-out .25s;  
 }

#posts td[class^="trow"] td[align$="t"] a img:hover { opacity:1;
  -webkit-transition: opacity 0s ease-out; 
     -moz-transition: opacity 0s ease-out;  
      -ms-transition: opacity 0s ease-out;  
       -o-transition: opacity 0s ease-out;  
          transition: opacity 0s ease-out;  
 }


#posts .tborder td[class^="trow"] td span.smalltext a { margin:0 0 0 5px !important; }

#posts .tborder td[class^="trow"] td a { margin: 0 5px 0 0 !important; }


/* Post Content */
/* Lets remove shit that we don't need */
/* Each reply automatically has an "RE: Title" at the top. That's stupid, lets remove it. */
#posts .tborder td[class^="trow"] td > span.smalltext > strong { display:none !important; }
/* There's a stupid BR before the content begins, nuke it. */
#posts .tborder td[class^="trow"] td > span.smalltext:nth-child(2) + br { display:none !important; }
/* Post #, kill it. */
#posts .tborder td[class^="trow"] td > div[style^="float:"]:first-child { display:none !important; }
/* Adjust the padding on the content now that we've killed unnecessary garbage */
#posts .tborder td[class^="trow"] td > span.smalltext:nth-child(2) + br + div { padding:0 !important; }


#posts table.tborder tbody tr td[class^="trow"] { padding:10px !important; }





/* Quote Boxes */

#posts .tborder blockquote, #posts .tborder [class*="block"], .tborder blockquote {  font-size:85% !important; color:#6D6D6D !important; background-color: rgba(0,0,0,0.25) !important; border-color:rgba(0,0,0,0.5) !important; padding:10px !important;
  -webkit-box-shadow: 4px 4px 4px rgba(0,0,0,0.15); 
     -moz-box-shadow: 4px 4px 4px rgba(0,0,0,0.15); 
          box-shadow: 4px 4px 4px rgba(0,0,0,0.15); 
margin-bottom:20px !important;
margin-left:20px !important;
margin-right:20px !important;
margin-top:20px !important;
}

#posts .tborder blockquote + br { display:none; }

/* Quotes with large images in it should be reduced in size */
#posts .tborder blockquote img { max-width:200px !important; max-height:300px !important; }
#posts .tborder blockquote img:hover { max-width:auto !important; max-height:auto !important; }

#posts .tborder blockquote blockquote, #posts .tborder [class*="block"] { margin:10px !important; }

#posts .tborder blockquote *, #posts .tborder [class*="block"] * { background-color: transparent !important; }
html body #posts .tborder blockquote cite span { font-size:8px !important; color:#494949!important; } /* Quote Time */
html body #posts .tborder blockquote cite { border-color:#494949 !important; margin:0 0 10px 0 !important; } /* Poster Name */



/* Final Post Footer */
#posts + .tborder .tfoot { background:rgba(0,0,0,0.5) !important; padding:10px !important; }
#posts + .tborder .tfoot div strong { line-height:26px !important; }
#posts + .tborder { border: 0 !important;  margin-bottom:10px !important }





/* Quick Reply */
#quickreply_e tr td[class^="trow"] { padding:15px !important; }
#quickreply_e tr td[class^="trow"] div { width:100% !important; }
html body #quickreply_e tr td div textarea { display:block !important; padding:0 !important; background-color:#161616 !important; }



/* Inputs */
#quickreply_e .tfoot { text-align:right !important; padding: 0 15px 15px 0 !important; }
#quickreply_e input { text-shadow:none !important; background-color:#000 !important; padding:10px !important; text-transform:uppercase;font-weight:bold;font-size:9px !important; }
#quickreply_e input:hover { cursor:pointer !important; background-color: #FF9000 !important; }
#quickreply_e input:active{ background-color: #000!important; }

/* Page Footer */

.bottommenu { background-image:none !important; background-color:rgba(0,0,0,0.7) !important; margin-top:30px !Important; padding:0 !important; }
#copyright { font-size:75% !important; padding:10px !important; }
#copyright br:last-of-type { padding:0 !important; margin:0 !important; display:none; }
#footer_stuff { padding:0 !important; width:auto !important; background-color:rgba(0,0,0,.8) !important; }

.bottommenu div span.smalltext a { display:inline-block; padding:20px !important;  }





/* Thread Listing */

/* Fixes padding and background-color for the thread listing table */
.tborder[style="clear: both;"] { border-collapse:separate !important; padding:15px !important; background-color:#1E1E1E !important; }

html tr .trow_sep { background-color:rgba(0,0,0,0.2) !important; padding:10px !important; }

/* Category titles for thread listing (Author/Forum/Replies/Views) */
html body div#container div#content table.tborder tbody tr:nth-child(2) td.tcat { background-color:rgba(0,0,0,0.4) !important; padding:10px !important; }


/* Attached Files */

#posts .tborder[id^="post"] fieldset { display:inline !important; width:auto !important; border: 1px solid #494949 !important; outline:0 !important; }
#posts .tborder[id^="post"] fieldset legend strong { color:#494949  !important; font-size:9px !important; }
html #posts .tborder[id^="post"] fieldset a, fieldset img.attachment { border:0 !important; padding:0 !important; margin:0 !important; display:inline-block !Important; }





/* ______________________________________________________________________________ */
/* ############################# STYLE OPTION ################################### */
/*                                                                                */
/* Light Version */
/*[[color-theme]]*/


[Box Shadows] { }
table.tborder[id^="post"] { box-shadow:none !important; -webkit-box-shadow:none !important; -moz-box-shadow:none !important; }
#container {  
  -webkit-box-shadow: 0px 0px 14px rgba(0,0,0,0.25); 
     -moz-box-shadow: 0px 0px 14px rgba(0,0,0,0.25);  
          box-shadow: 0px 0px 14px rgba(0,0,0,0.25); }

#posts .tborder blockquote, #posts .tborder, .tborder blockquote, [class*="block"] {
  -webkit-box-shadow: 3px 3px 3px rgba(0,0,0,0.05); 
     -moz-box-shadow: 3px 3px 3px rgba(0,0,0,0.05); 
          box-shadow: 3px 3px 3px rgba(0,0,0,0.05); }


[Borders] { }
* { border-color:#E0E1D9 !important; }
table.tborder[id^="post"] { border-bottom: 3px solid #C2C9BE !important; }
table.tborder[id^="post"] { border-top: 2px solid #B3B9A4 !important; }
html { border-bottom:4px solid #FF9000 !important; }

#posts + .tborder .tfoot input { border:0 !important; }

#posts .tborder blockquote, #posts .tborder, .tborder blockquote, [class*="block"] { border-color:#cccccc !important; }




[Background Colors] { }
#container { background-color:#fff !important; }

.tborder[style="clear: both;"] { background-color:#fbfbfb !important; }

.tborder, .tborder tr, .tborder td { background-color: #EDEFEF !important; }
#posts > table:nth-child(2n), #posts > table:nth-child(2n) tr, #posts > table:nth-child(2n) td { background-color:#EDEFEF !important; }

#posts .tborder [class^="trow"]:first-child { background-color:#EDEFEF !important; }

.bottommenu { background-color:#3F89FF  !important; }
#copyright { background-color:#2D77E5 !important; }

html body #quickreply_e tr td div textarea, textarea { background-color: #fff !important; }
input.textbox { background-color: #fff !important; }
select { background-color: #fff !important; }

#posts .tborder[id^="post"] tr:last-child td[class^="trow"] { background-color: #DFE3E3 !important; }
#posts .tborder[id^="post"] tr:last-child td[class^="trow"] > table > tbody > tr > td, #posts .tborder[id^="post"] tr:last-child td[class^="trow"] > table > tbody > tr { background-color: transparent !important; }

#posts + .tborder .tfoot { background-color: #3F89FF  !important; }
#posts + .tborder .tfoot input { background-color: #0d2b5b !important; }


input[class="button"], #quickreply_e input[class="button"] { background-color: rgba(0,0,0,0.15) !important; text-shadow: none !important; }
input[class="button"]:hover, #quickreply_e input[class="button"]:hover { background-color: #ff9000 !important; color:#fff !important; }



#posts .tborder blockquote, #posts .tborder, .tborder blockquote, [class*="block"] { background-color:rgba(255,255,255,0.5) !important; }

html body div#container div#content table.tborder tbody tr:nth-child(2) td.tcat { background-color: rgba(255, 255, 255, .5) !important; }

html tr .trow_sep { background-color:#3F89FF !important; color:#fff !important; }


[Font Colors] { }
* { color:#576581 !important; }
#copyright { color:#fff !important; }

#posts + .tborder .tfoot * { color:#335ea4 !important; }
#posts + .tborder .tfoot a { color: #fff !important; }
#posts + .tborder .tfoot input { color: #fff !important; }


[Link Colors] { }
#copyright a { color:#133F82 !important; }
.bottommenu a { color:#fff !important; }


[Padding and Spacing] { }
#posts .tborder[id^="post"] tr:first-child td[class^="trow"] { padding-bottom:10px !important; }
#pagemenucontainer { margin: 0 !Important; }


/* */
/* ########## END OF STYLE OPTION ######### */



[Rounded Borders] { }
#container { 
  -webkit-border-radius: 3px; -moz-border-radius: 3px; border-radius: 3px; 
  -moz-background-clip: padding; -webkit-background-clip: padding-box; background-clip: padding-box; }

input, textarea, select {   -webkit-border-radius: 3px; -moz-border-radius: 3px; border-radius: 3px;  }

/* CLOUDCHASERRRRRRRRRRRRRRRRRRRRRRRRRRRRRRR! */

body { background-image:none !important; background-color:#0069B3 !important; }

#games_background { background-image:none !important; background-color:transparent !important; height:auto !important; }
#menu_container { display:block !important; height:111px !important; padding:0 !important;

background-repeat: no-repeat !important;
background-image: url(data:image/jpeg;base64,)  !important; 
background-position:top left;
}




}
