/***
     Mozillazine Dark by dnast
     First posted Oct-07-2011
     Last  edited Nov-02-2011
***/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(forums.mozillazine.org) {


body, body > table {
  color: #fff !important;
  background-color: #000 !important;
  font: 9pt Helvetica,sans-serif !important;
}

a {
  border-bottom: none !important;
  font-family: Helvetica,sans-serif !important;
  font-weight: 500 !important;
}

a:hover {
  color: #59f !important;
}

/*** Forum  Index ***/
ul.forums > li:nth-child(even) {
  background-color: #050505 !important;
}

ul.forums > li:nth-of-type(odd) {
  background-color: #151515 !important;
}

div.forabg, div.forumbg, div.topic-actions {
  margin-top: 1.5em !important;
  background: #050505 !important;
  padding: .5em !important;
}

div.forabg dl, div.forabg dl  {
  margin-left: 1em !important;
  padding-top: .5em !important;
}


/*** Forums ***/
div#page-body > h2 + div {
  display: none !important;
  background: none !important;
}

div.topic-actions div.search-box {
  border: 0px solid red;
  margin-top: .4em !important;
}

ul.topiclist:first-child > li.header {
  background: #000 !important;
}

ul.topiclist > li:nth-child(even) {
  background-color: #050505 !important;
}

ul.topiclist > li:nth-of-type(odd) {
  background-color: #151515 !important;
}

ul.topiclist > li:not(last-child) {
  border-top: none !important;
  border-bottom: 1px solid rgba(255,255,255,.30) !important;
}

.forums > li:last-child, .topics > li:last-child {
  border: none !important;
}

.forumbg {
  margin-top: 2em !important;
  background: #000 !important;
}

a.topictitle {
  font-weight: bold !important;
}

a.topictitle:hover, a.forumtitle:hover {
  /* color: #36a !important; */
  color: #59f !important;
  text-decoration: none !important;
}


li.row dd {
  border: none !important;
}

li.row:last-child {
  border-radius: 0 0 5px 5px !important;
}

ul.forums {
  background: #000 !important;
}


/*** Topic Threads ***/

#page-body > div {
  border-radius: 8px !important;
  border: 1px solid #333 !important;
}

#page-body > div.post:nth-of-type(2n) {
  background-color: #050505 !important;
}

#page-body > div.post:nth-of-type(2n+1) {
  background-color: #191919 !important;
}


.postprofile {
  border-right: 1px solid rgba(255,255,255,.25) !important;
}


.corners-top, .corners-bottom {
  display: none !important;
}

.content {
  color: #ddd !important;
  font-family: helvetica,sans-serif !important;
}

.content > blockquote {
  color: #ddd !important;
  background-color: #444 !important;
  border-radius: 5px !important;
}

blockquote blockquote {
  background-color: rgba(255,255,255,.2) !important;
  border-radius: 5px !important;
}

blockquote a {
  color: #ccc !important;
  border-bottom: 1px solid #6af !important;
}

.author, .signature {
  color: #999 !important;
}

.post_body > .notice {
  color: #666
}

.inner strong {
  color: #999 !important;
}

#postingbox {
  background-color: #151515 !important;
}

#postingbox textarea {
  border-radius: 5px !important;
  background-color: #ddd !important;
}

#postform > .panel, .navbar, div.sidebar {
  background-color: #000 !important;
}

div.sidebar {
  background-color: #151515 !important;
  color: white !important;
  border: 1px solid #555 !important;
  border-radius: 5px !important;
  font-size: 100% !important;
}

div.sidebar a {
  color: #60a0fd !important;
}

form#forum-search input {
  font-size: 80% !important;
}

.post-icon {
  margin: 2px 0 !important;
}

/* Highlights new posts */

a + a.topictitle {
  color: #59f !important;
  color: #36a !important;
  color: #36a !important;
}


.pagination {
  margin-right: -.25em !important;
}

.pagination span a {
  background: none !important;
  border: 1px solid #444 !important;
  color: #888 !important;
}

.pagination span a:hover {
  background: #124 !important;
  border: 1px solid #249 !important;
  color: #7af !important;
}

.pagination span strong, .pagination span a:active {
  background: #444 !important;
  border-color: #777 !important;
  color: white !important;
}


/** Posts/Posting **/
.post {
  color: #ddd !important;
  background-color: #252525 !important;
}

span[style="color: black"] {
  color: white !important;
}



/******************/
/*** Test Icons ***/
/******************/

/* Topic List */
ul.forums dl.icon {
  background-image: url("data:image/png;base64,") !important;
  background-position: .2em 50% !important;
}

ul.topics dl.icon {
  background-position: .75em 50% !important;
}

/* Topic */
ul.topics > li > dl.icon,
ul.topics > li > dl.icon[style^="background-image: url(./styles/prosilver/imageset/topic_read.gif)"] {
  background-image: url("data:image/png;base64,") !important;
}

/* Topic - unread & posted in */
ul.topics > li > dl.icon[style^="background-image: url(./styles/prosilver/imageset/topic_unread_mine.gif)"],
ul.topics > li > dl.icon[style^="background-image: url(./styles/prosilver/imageset/topic_read_mine.gif)"] {
  background-image: url("data:image/png;base64,") !important;
}

/* Topic - Hot */
ul.topics > li > dl.icon[style^="background-image: url(./styles/prosilver/imageset/topic_unread_hot.gif)"],
ul.topics > li > dl.icon[style^="background-image: url(./styles/prosilver/imageset/topic_read_hot.gif)"] {
  background-image: url("data:image/png;base64,") !important;
}

/* Topic - Hot & posted in */
ul.topics > li > dl.icon[style^="background-image: url(./styles/prosilver/imageset/topic_unread_hot_mine.gif)"],
ul.topics > li > dl.icon[style^="background-image: url(./styles/prosilver/imageset/topic_read_hot_mine.gif)"] {
  background-image: url("data:image/png;base64,") !important;
}

/* Topic - Locked */
ul.topics > li > dl.icon[style^="background-image: url(./styles/prosilver/imageset/topic_unread_locked.gif)"],
ul.topics > li > dl.icon[style^="background-image: url(./styles/prosilver/imageset/topic_read_locked.gif)"] {
  background-image: url("data:image/png;base64,") !important;
}

/* Topic - Locked and posted in */
ul.topics > li > dl.icon[style^="background-image: url(./styles/prosilver/imageset/topic_unread_locked_mine.gif)"],
ul.topics > li > dl.icon[style^="background-image: url(./styles/prosilver/imageset/topic_read_locked_mine.gif)"] {
  background-image: url("data:image/png;base64,") !important;
}




/* Sticky */
ul.topics > li > dl.icon[style^="background-image: url(./styles/prosilver/imageset/sticky_unread.gif)"],
ul.topics > li > dl.icon[style^="background-image: url(./styles/prosilver/imageset/sticky_read.gif)"] {
  background-image: url("data:image/png;base64,") !important;
}

/* Sticky - Posted in [not fixed] */
ul.topics > li > dl.icon[style^="background-image: url(./styles/prosilver/imageset/sticky_unread_mine.gif)"] {
  background-image: url("data:image/png;base64,") !important;
}

/* Sticky - Locked */
ul.topics > li > dl.icon[style^="background-image: url(./styles/prosilver/imageset/sticky_unread_locked.gif)"],
ul.topics > li > dl.icon[style^="background-image: url(./styles/prosilver/imageset/sticky_read_locked.gif)"] {
  background-image: url("data:image/png;base64,") !important;
}

}
