style.css:
@-moz-document domain("habrahabr.ru") {
.sidebar_right { display: none !important; }
.content_left, .company_post{width: 100% !important; max-width: 100% !important; }
#layout {padding: 0px !important; width: 90% !important; max-width: 90% !important; }
.logo {height:30px !important; background:none !important;}
}
@-moz-document url-prefix("http://habrahabr.ru/blogs") {
#header, .sidebar_right, .blog_header, #footer, .add_post, .menu, .submenu, .tags, .infopanel, .comments_list, .comments_form { display: none !important; }
.content_left, .company_post{width: 100% !important; max-width: 100% !important; }
#layout {padding: 0px !important; width: 90% !important; max-width: 90% !important; }
}
