style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("bashtube.ru") {

/* фон --------------------------------------------- */

/*[[background]]*/

/* общее и главная --------------------------------------------- */

.maint,.top_preview,#center_right {background:transparent !important;}

a {color: #006699 !important;}
a:hover {color: #DD6900 !important;}
h2 {color: #DD6900 !important;}
h2 a {color: #DD6900 !important;}
.tags a {color: #1E1E1E !important;}
.tags a:hover {color: #006699 !important;}
#testinfo a {color: #DD6900 !important;}
#testinfo a:hover {color: #006699 !important;}
#copyright a {color: #000 !important;}
TD[style="background-color: #e8e8e8"] {
background-image: -moz-linear-gradient(left , rgba(255, 255, 255,.7), rgba(255, 222, 173,.3)) !important;
background-image: -webkit-linear-gradient(left , rgba(255, 255, 255,.7), rgba(255, 222, 173,.3)) !important;
border-bottom-left-radius: 3px;border-top-left-radius: 3px;
background-color: transparent !important;
}
TD[style="background-color: #e8e8e8"][class="value"] {
background-image: -moz-linear-gradient(right , rgba(255, 255, 255,.7), rgba(255, 222, 173,.3)) !important;
background-image: -webkit-linear-gradient(right , rgba(255, 255, 255,.7), rgba(255, 222, 173,.3)) !important;
border-bottom-right-radius: 3px;border-top-right-radius: 3px;
background-color: transparent !important;
}

#avatara img,.avatar img {border-radius: 7px !important;}

#name > P > A {padding: 3px 10px 3px 10px;
border-bottom-left-radius: 3px;border-top-right-radius: 3px;border-bottom-right-radius: 18px;border-top-left-radius: 18px; border: 1px solid #DCDCDC;
background-image: -moz-linear-gradient(top, rgba(255, 222, 173,.5), rgba(244, 164, 96,.5)) !important;
background-image: -webkit-linear-gradient(top , rgba(255, 222, 173,.5), rgba(244, 164, 96,.5)) !important;
text-decoration:none !important;
color: #1E1E1E !important;
  box-shadow: 0 1px 0 rgba(255,255,255,.25) inset,
              0 0 0 1px rgba(255,255,255,.25) inset;
                text-shadow: 0 0 1px rgba(255,255,255,.7),
               0 1px 1.5px rgba(255,255,255,.5);         
}

#name > P > A:hover:not(:active) {
border: 1px solid #C0C0C0;
background-image: -moz-linear-gradient(top, rgb(135, 206, 235), rgb(70, 130, 180)) !important;
background-image: -webkit-linear-gradient(top, rgb(135, 206, 235), rgb(70, 130, 180)) !important;
color: #FFFAFA !important;
  box-shadow: 0 1px 0 rgba(255,255,255,.1) inset,
              0 0 4px 1px rgba(230,230,230,.7) inset,
              0 -1px 0 rgba(230,230,230,.5) inset;
                text-shadow: 0 0 1px rgba(0,0,0,.7),
               0 1px 1.5px rgba(0,0,0,.5);
}

#name > P > A:hover:active {
border: 1px solid #C0C0C0;
background-image: -moz-linear-gradient(bottom, rgb(95, 166, 195), rgb(30, 90, 140)) !important;
background-image: -webkit-linear-gradient(top, rgb(135, 206, 235), rgb(70, 130, 180)) !important;
color: #FFFAFA !important;
  box-shadow: 0 2px 3px rgba(0,0,0,.4) inset,
              0 1px 1px rgba(0,0,0,.2) inset;
                text-shadow: 0 0 1px rgba(0,0,0,.7),
               0 1px 1.5px rgba(0,0,0,.5);
}

.item {background-image: none !important; border-bottom: 1px solid #A9A9A9 !important;}

.item .text {padding: 5px 0 0 7px !important;
border-top: 1px solid #DCDCDC !important;
border-left: 1px solid #DCDCDC !important;
border-radius: 3px !important;
background-image: -moz-linear-gradient(-45deg , rgb(255, 255, 255), rgba(235, 235, 235,.1)) !important;
background-image: -webkit-linear-gradient(-45deg , rgb(255, 255, 255), rgba(235, 235, 235,.1)) !important;
}

.back_preview {background: transparent !important;}
.img_preview img {border-radius: 3px !important;}


/* комментарии --------------------------------------------- */

.comments td.text {
border-top: 1px solid #DCDCDC !important;border-bottom: 1px solid #C0C0C0 !important;border-right: 1px solid #D3D3D3 !important;border-left: 1px solid #D3D3D3 !important;
border-bottom-left-radius: 0px !important;border-top-left-radius: 0px !important;
background-image: -moz-linear-gradient(top , rgb(248, 248, 248), rgb(248, 248, 248)) !important;
background-image: -webkit-linear-gradient(top , rgb(248, 248, 248), rgb(248, 248, 248)) !important;
border-radius: 3px !important;
padding: 5px 0 5px 7px !important;
}

.comments td.cmt-bot {border-bottom: none !important;}
.comments td.avatar {padding-top: 5px !important;padding-right: 10px !important;
background-image: -moz-linear-gradient(left , rgb(235, 235, 235), rgb(255, 255, 255)) !important;
background-image: -webkit-linear-gradient(top , rgb(235, 235, 235), rgb(255, 255, 255)) !important;
border-bottom-left-radius: 3px !important;border-top-left-radius: 3px !important;
border-bottom: 1px solid #D3D3D3 !important;border-left: 1px solid #DCDCDC !important;border-top: 1px solid #DCDCDC !important;
}


/* поиск --------------------------------------------- */

#search, #search2 {background-color: transparent !important;
background-image: -moz-linear-gradient(left, rgba(255, 222, 173,.5), rgba(244, 164, 96,.5)) !important;
background-image: -webkit-linear-gradient(left , rgba(255, 222, 173,.5), rgba(244, 164, 96,.5)) !important;
border-radius: 7px !important;
}
#search input {-moz-appearance:none !important;-webkit-appearance:none !important;background-color: #FFFAFA !important;border: 1px solid #DCDCDC !important; border-radius: 3px !important;}
#search input:not(:focus):not(:hover) {background-color:transparent !important;
background-image: -moz-linear-gradient(bottom , rgb(245, 245, 245), rgb(220, 220, 220)) !important;
background-image: -webkit-linear-gradient(bottom , rgb(245, 245, 245), rgb(220, 220, 220)) !important;
}

.search-bttn:before {
    content: "Найти";
    font-size: 16px;
    font-weight:bold;
    visibility: visible;}

.search-bttn {background:transparent !important;background-position: 0 0px !important;
color: #FFFACD !important;text-align: center;line-height: 37px !important;text-decoration:none !important;
                text-shadow: 0 0 1px rgba(0,0,0,.7), 0 1px 1.5px rgba(0,0,0,.5);
}
.search-bttn:hover:not(:active) {color: #FFFAFA !important;text-shadow: 0 0 3px rgba(0,0,0,.7), 0 1px 1.5px rgba(0,0,0,.5);}
.search-bttn:hover:active {color: #FFFAFA !important;text-shadow: 0 0 3px rgba(128,0,0,.7), 0 1px 1.5px rgba(0,0,0,.5);}

/* копирайт --------------------------------------------- */

HTML > BODY > TABLE > TBODY > TR:last-child > TD {background-image: none !important;}

#bottom {
background-image: -moz-linear-gradient(left, rgba(255, 222, 173,.5), rgba(244, 164, 96,.5)) !important;
background-image: -webkit-linear-gradient(left , rgba(255, 222, 173,.5), rgba(244, 164, 96,.5)) !important;
border-radius: 7px !important;
border: 1px solid #D3D3D3 !important;
}

/* лого --------------------------------------------- */

#logo > A > IMG {
    opacity: 0;
    margin-left: -272px !important; 
    position: absolute !important; 
}
#logo > A:before {
    content: url('http://linkme.ufanet.ru/images/7fc1cbb3333c92bc0bc9d7bf8f3fa27e.png');
}

/* Добавить видео --------------------------------------------- */

#add > A > IMG {
    display: none;}
#add > A:before {
    content: "Добавить видео";
    font-size: 17px;
    visibility: visible;}
#add {text-align: center;line-height: 41px !important;}

#add > a {padding: 12px 8px 8px 8px;
border-radius: 5px; border: 1px solid #DCDCDC;
background-image: -moz-linear-gradient(top , rgba(249, 249, 249,.7), rgba(229, 229, 229,.7)) !important;
background-image: -webkit-linear-gradient(top , rgba(249, 249, 249,.7), rgba(229, 229, 229,.7)) !important;
text-decoration:none !important;
  box-shadow: 0 1px 0 rgba(255,255,255,.25) inset,
              0 0 0 1px rgba(255,255,255,.25) inset;
                text-shadow: 0 0 1px rgba(255,255,255,.7),
               0 1px 1.5px rgba(255,255,255,.5);         
}

#add > a:hover:not(:active) {
border: 1px solid #C0C0C0;
background-image: -moz-linear-gradient(top, rgb(135, 206, 235), rgb(70, 130, 180)) !important;
background-image: -webkit-linear-gradient(top, rgb(135, 206, 235), rgb(70, 130, 180)) !important;
color: #FFFAFA !important;
  box-shadow: 0 1px 0 rgba(255,255,255,.1) inset,
              0 0 4px 1px rgba(230,230,230,.7) inset,
              0 -1px 0 rgba(230,230,230,.5) inset;
                text-shadow: 0 0 1px rgba(0,0,0,.7),
               0 1px 1.5px rgba(0,0,0,.5);
}

#add > a:hover:active {
border: 1px solid #C0C0C0;
background-image: -moz-linear-gradient(bottom, rgb(95, 166, 195), rgb(30, 90, 140)) !important;
background-image: -webkit-linear-gradient(top, rgb(135, 206, 235), rgb(70, 130, 180)) !important;
color: #FFFAFA !important;
  box-shadow: 0 2px 3px rgba(0,0,0,.4) inset,
              0 1px 1px rgba(0,0,0,.2) inset;
                text-shadow: 0 0 1px rgba(0,0,0,.7),
               0 1px 1.5px rgba(0,0,0,.5);
}

/* Уфанет Паспорт --------------------------------------------- */

#passport > A > IMG {
    display: none;}

#passport > A:before {
    content: "Войти : Уфанет Паспорт";
    font-size: 17px;
    visibility: visible;}
#passport {text-align: center;line-height: 41px !important;}

#passport > a {padding: 12px 8px 8px 8px;
border-radius: 5px; border: 1px solid #DCDCDC;
background-image: -moz-linear-gradient(top, rgb(255, 222, 173), rgb(244, 164, 96)) !important;
background-image: -webkit-linear-gradient(top , rgb(255, 222, 173), rgb(244, 164, 96)) !important;
text-decoration:none !important;
  box-shadow: 0 1px 0 rgba(255,255,255,.25) inset,
              0 0 0 1px rgba(255,255,255,.25) inset;
                text-shadow: 0 0 1px rgba(255,255,255,.7),
               0 1px 1.5px rgba(255,255,255,.5);
}

#passport > a:hover:not(:active) {
border: 1px solid #C0C0C0;
background-image: -moz-linear-gradient(top, rgb(135, 206, 235), rgb(70, 130, 180)) !important;
background-image: -webkit-linear-gradient(top, rgb(135, 206, 235), rgb(70, 130, 180)) !important;
color: #FFFAFA !important;
  box-shadow: 0 1px 0 rgba(255,255,255,.1) inset,
              0 0 4px 1px rgba(230,230,230,.7) inset,
              0 -1px 0 rgba(230,230,230,.5) inset;
                text-shadow: 0 0 1px rgba(0,0,0,.7),
               0 1px 1.5px rgba(0,0,0,.5);
}

#passport > a:hover:active {
border: 1px solid #C0C0C0;
background-image: -moz-linear-gradient(bottom, rgb(95, 166, 195), rgb(30, 90, 140)) !important;
background-image: -webkit-linear-gradient(top, rgb(135, 206, 235), rgb(70, 130, 180)) !important;
color: #FFFAFA !important;
  box-shadow: 0 2px 3px rgba(0,0,0,.4) inset,
              0 1px 1px rgba(0,0,0,.2) inset;
                text-shadow: 0 0 1px rgba(0,0,0,.7),
               0 1px 1.5px rgba(0,0,0,.5);
}

/* загрузка --------------------------------------------- */

#bashtubeuploader-upload {
border-radius: 5px; border: 1px solid #DCDCDC;
background-image: -moz-linear-gradient(top , rgb(249, 249, 249), rgb(229, 229, 229))!important;
background-image: -webkit-linear-gradient(top , rgb(249, 249, 249), rgb(229, 229, 229))!important;
text-decoration:none !important;
  text-shadow: 0 0 1px rgba(255,255,255,.7),
               0 1px 1.5px rgba(255,255,255,.5);
}
#bashtubeuploader-upload:hover:not(:active) {
background-image: -moz-linear-gradient(top, rgb(255, 222, 173), rgb(244, 164, 96)) !important;
background-image: -webkit-linear-gradient(top , rgb(255, 222, 173), rgb(244, 164, 96)) !important;
background-position: 0 0px !important;
color: #1E1E1E !important;
  box-shadow: 0 1px 0 rgba(255,255,255,.1) inset,
              0 0 4px 1px rgba(230,230,230,.7) inset,
              0 -1px 0 rgba(230,230,230,.5) inset;
}
#bashtubeuploader-upload:hover:active {
background-image: -moz-linear-gradient(bottom, rgb(235, 202, 153), rgb(224, 144, 46)) !important;
background-image: -webkit-linear-gradient(top , rgb(255, 222, 173), rgb(244, 164, 96)) !important;
background-position: 0 0px !important;
color: #1E1E1E !important;
box-shadow: 0 2px 3px rgba(0,0,0,.4) inset,
              0 1px 1px rgba(0,0,0,.2) inset;
}
#bashtubeuploader-upload:before {
    content: "Загрузить";
    font-size: 17px;
    visibility: visible;}
#bashtubeuploader-upload {text-align: center;line-height: 37px !important;}
#file-info {
background-image: -moz-linear-gradient(-45deg , rgb(255, 255, 255), rgba(235, 235, 235,.1)) !important;
background-image: -webkit-linear-gradient(-45deg , rgb(255, 255, 255), rgba(235, 235, 235,.1)) !important;
padding: 5px !important; border-radius: 3px !important;
}

/* не работает ------------------------------------------ */

#addlink,#loginform {display: none !important;}

/* реклама -------------------------------------------- */

/*[[banners]]*/

/* инфо ----------------------------------------------- */

/*[[infoufanet]]*/

}

options.value:
#second_right,
#second_right_min,
#second_right_min2 {
border-radius: 7px !important;
background-image: -moz-linear-gradient(top , rgba(105, 105, 105,.8), rgba(192, 192, 192,.8)) !important;
background-image: -webkit-linear-gradient(top , rgba(105, 105, 105,.8), rgba(192, 192, 192,.8)) !important;
}
