style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.rd.com") {

body { 
   background-image: none !important; 
}

.mboxDefault, #capsuleHeader, #capsuleFooter, #omniture, #footerBtmRow,
#capsule > tbody > tr > td:first-child + td,
.leftNavTemplate, #rightCol, #mailEditorSection, #advertisementIsland,
#RdIcons
  { display: none !important; }

#capsuleContent, #capsule,
#capsule > tbody > tr > td:first-child,
.wellContentTemplate, #articleContainer
{ 
   width: 100% !important;
   max-width: none !important;
   margin: 0 !important;
   padding: 0 !important;
   float: none !important;
}

#content
{
   padding: 2% !important;
   width: 96% !important; 
   text-align: justify !important;
   font-size: 12pt !important;
   font-family: arial, sans-serif !important;
   max-width: none !important;
}


}
