style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.iskrica.com"),
               domain(www.iskrica.com),
               url-prefix(http://www.iskrica.),
               url-prefix(http://iskrica.)
{
body {background-image: none !important;color:#FFF}
#im_status {background-color:#FFF !important}

/* BIJELI TEXT */
.field_box,
.reg_user_info_th1 h4 a,
.reg_user_info_th2 h4 a,
.reg_user_info_th3 h4 a,
.reg_user_profiles h4,
div#center h1 {color:#FFF !important}

/* UBIJ POZADINSKE SLIKE */
#logo, #center, #center_container, #right, #center_top, #right_top, #footer, h3.premium_services,a.member_search, a.vip, a.msg, #premium_submenu_container, #premium_submenu_bottom, .reg_user_profiles, ul#menu li, ul#premium_submenu li, .clubs ul, table.reg_user_info, #field_box_x, #new_members_container, #new_members_container a, #new_members_bottom, .new_members, div.iskrica_info
{background-image:none !important;border:0 none !important}

/* UBIJ SAMO BOJU POZADINE */
.weblog h6, .clubs h6, .dossier h6, .tip, .prev, .next, .premium_notice {background-color:#FFF !important;border:0 none !important}

/* UBIJ FORMU NA NASLOVNOJ */
.field_box form p select {color:#FFF !important;border:0 none !important}

/* PRETRAGE VELIKO */
.saved_search {text-transform:uppercase !important}

/* NOVA PORUKA*/
span.msg_no_notice {font-size:14px !important;color:#000 !important;line-height:26px !important}
div#center h1.profile {color:#999 !important}

/* MAKNI GLUPI LOGO */
img.logo {display:none}

}
