/* 
Name: Icerocket-Twitter - stylish Plugin
Version: 0.9
Author: Rodrigo Alves
Contact: rvalves[at]gmail[dot].com 
Description: Theme for reporting purposes.
The original layout is not good for compile reports, this custom CSS have more widely spaced lines, highlighted followers and remove "Reply/RT" links.
*/

@-moz-document domain("http://www.icerocket.com/"),url-prefix("http://www.icerocket.com/search?tab=twitter"){
	table#vrts1, div#footer{ display:none !important; }
	
	table.content{ border-bottom:2px solid #95C6FF !important; padding:10px 0 !important; }
	table.content a.twitinfo{ color:#F00 !important; font-weight:bold !important;}
	table.content a.author{ display:none !important; }
}
