style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.sanego.de") {

/* Stimmungsbox korrigieren */
div.userEmotion span {
    width: 100% !important; }
div.userEmotion p {
    font-size: 95%;
    font-style: italic; }
a#update-emotion {
    display: inline !important; }

/* Antwortbereich anpassen */
table.questionAnswers p.a {
    min-height: 80px; }
table.questionAnswers td {
    border-bottom: 1px solid #ccc; }
table.questionAnswers tr:last-child td {
    border-bottom: none; }

/* Missbrauch-Buttons ausblenden
 - [Missbrauch melden]
 - [Bild unpassend?]    */
a.spamable { display: none; }

}
