style.css:
@-moz-document domain("phandroid.com") {
div.pinch, div#frame_box,div#temp_Content_Right, div#am_sidebox, tr.ad300, table#holidayAds, iframe, div#globalFooter, div#temp_Footer > p:nth-of-type(1) {display:none !important;}
div#temp_footer {margin-bottom:0px !important;}
div#temp_content_left {width:auto !important;}
#temp_content_left .inner .post {padding-bottom:0px !important; margin-bottom: 10px !important;}
#temp_content_left .inner .post .content {padding-top:10px !important;}
div.content > img[src*="/wp-content/"] {padding:0 10px 10px 0 !important; height:65px !important;}
div.content > p:nth-of-type(1) {margin: 0 0 0 0 !important;}
h2.storytitle {font-size: 1.1em !important;}
}
