style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("heise.de") 
/*basiert auf dem css von alex/ bases on this skript: http://my.opera.com/alexs/blog/user-stylesheet-heise-de*/
{

/* entfernt auf saemtlichen Unterseiten Werbung */
.kasten_markt,
div.adbottom,
div.adbottom_itmarkt,
div.bcadv_start,
div.contentbanner,
div.heiseadvert,
div.ISI_IGNORE,
div.leaderboard,
div.skyscraper,
iframe[src*="/ads."],
iframe[name="geizhals.at"],
img[src*="/viewad/"],
img[src*="/RealMedia/"],
embed[type="application/x-shockwave-flash"],
table.druck[border="0"][cellspacing="0"][cellpadding="0"][align="center"] {
	display:none !important;
}


/* Schrifteinstellungen - Schriftart und Groesse */

table[border="0"][cellspacing="0"][cellpadding="0"][width="100%"],
h2, h3.anriss, .rand_links a {
	font-family:Bitstream Vera Sans !important;
}

/*table[border="0"][cellspacing="0"][cellpadding="0"][width="100%"] {
	font-size:1.05em !important;
}*/

h2,h3.anriss	{font-size:1.1em !important;}
.rand_links a	{font-size:0.87em !important;}

.f-content DIV.forum_titel H1 , .f-content DIV.forum_titel P , .posting_text ,   .fliess , .zitat , .thread_tree, .fora_list , .forum_news
   	{ font-size: 1em !important;}  
.posting_subject 
            {font-size: 1.2em !important;}  
.forum_pfad, .forum_navi, .kasten_news, .forum_inhalt, .forum_aktion, .forum_start, .forum_eingang 
            { font-size: 0.9em !important;}

body { font-family: Bitstream Vera Sans; !important }

}
