/* ==UserStyle==
// @name         LR FFX3 Shrink bookmarks/folders to icons unless hovered
// @author       LRodman
// @version      1.1
// ==/UserStyle==*/

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#bookmarksBarContent toolbarbutton:not([container="true"]) label {
display: none !important; }
#bookmarksBarContent toolbarbutton:not(:hover) label {
width: 32px!important; }

#bookmarksBarContent toolbarbutton:not([container="true"]):hover label {
display: block !important; }
