style.css:
@-moz-document domain('mail.google.com') {

/* Eliminate the icons */
.ar8, .asl, .ar9, .ase, .asb, .asf {
  background: none !important;
  margin-bottom: 4px !important;
  opacity: 1 !important;
}

/* Archive button */
.ar8:after {
  content: "Archive";
  margin-left: -11px;
}

/* Report spam button */
.asl:after {
  content: "Spam";
  margin-left: -6px;
}

/* Trash button */
.ar9:after {
  content: "Trash";
  margin-left: -7px;
}

/* Move button */
.ase:after {
  content: "Move";
}
.ase {
  margin-right: 9px;
}

/* Manage labels button */
.asb:after {
  content: "Labels";
}
.asb {
  margin-right: 16px;
}

/* Refresh button */
.asf:after {
  content: "Refresh";
  margin-left: -12px;
}

}
