style.css:
/*
 * VK image minifier
 *
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('vk.com'), domain('vkontakte.ru') {
.wall_module .post_thumbs_num1 .page_media_thumb1 img, .wall_module .post_thumbs_num3 .page_media_thumb1 img, .wall_module .post_thumbs_num4 .page_media_thumb1 img, .wall_module .post_thumbs_num2 .page_media_thumb1 img, .wall_module .post_thumbs_num2 .page_media_thumb2 img {
    max-height: 97px !important;
    max-width: 130px !important;
}

.wide_wall_module .post_thumbs_num1 .page_media_video .video img, .wall_module .page_media_full_video .video img {
    max-height: 97px !important;
    max-width: 130px !important;
}
}
