style.css:
@-moz-document domain("youtube.com") {
	#body-container #player,
	#body-container #player-api {
		width: 100% !important;
		max-width: 1280px !important;
		margin: auto !important;
		height: 750px !important;
	}
	
	#body-container .html5-main-video {
		height: 100% !important;
		width: 100% !important;
	}
	
	#watch7-sidebar {
		margin-top: 0px !important;
		padding-top: 10px !important;
	}
	
	/* Disables playlists */
	
	#playlist,
	.watch7-playlist-bar-left {
		width: 100% !important;
	}
	
	.watch7-playlist-bar-right {
		-moz-transition: opacity .3s ease-in !important;
		opacity: 0 !important;
		left: -400px !important;
		width: 400px !important;
	}
	
	.watch7-playlist-bar-left:hover + .watch7-playlist-bar-right,
	.watch7-playlist-bar-right:hover {
		opacity: 1 !important;
	}

	.ended-mode .html5-scrubber-button {
			left: 99.3% !important;
	}
	
	/* html5 player */
	
	.html5-video-content {
		height: 100% !important;
		width: 100% !important;
	}
	
	.html5-scrubber-button {
		display: none !important;
	}
	
	/* 
	 *
	 * Youtube - Lights off! fix
	 *
	 * Lights off script has to be enabled before you enable this script!
	 * Otherwise it won't work.
	 *
	 */
	 
	.exp-new-site-width #page {
		width: auto !important;
	}
}
