style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("strobist.blogspot.com") {

  #navbar-iframe {
    display: none;
  }

  body #header {
    margin-top:10px;
  }

  a#top-banner{
    display: none;
  }

  #content {
    width:960px !important;
  }

  #main {
    width: 642px !important;
  }

  div#sidebar2 select  {
    width: 90%;
  }

  div#sidebar2 br {
    display: none;
  }

  div#sidebar2 embed {
    display: none;
    /* no flash in sidebar !!!! */
  }

  div#sidebar2 h6 + ul + ul {
    display: none;
  }


}
