style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("google.fr"), domain("google.com") {

/* == GOOGLE for GM  "Google Real Side Preview" V.2 == */

/* TEST - LINK DISPLAY ON IFRAME -
.l:active:after{
content:" (" attr(href) ")";
display:block !important;
position:absolute !important;

} === */

/* RESULTS TOP BOX - TOPNAV LINKS SCRIPT IN ONE LINE - 
 "Top5 Open/Close/All Open/Close/Top5 Tab/All Tab" - === */
#resultStats>br {
    display: none !important;
}

/* coppy in MY USERSTYLE PERSO : "GOOGLE - FR - TIME Search + tweak divers" -
#resultStats {
    display: inline-block !important;
    height: 95px !important;
width : 800px !important;
z-index : 13000 !important;
} === */
/* RESULTS PANEL - for Normal (#GoogleTabledResults) and "Google Real Side Preview" (#ires) -/
#GoogleTabledResults {
    margin-top: 168px !important;
} === *
#ires {
margin-left: 31px !important;
    margin-top: -103px !important;
}


/* RESULT SEARCH NUMBER + TOPNAV SCRIPT "Google Real Side Preview" - === */
#resultStats > a {
    color: black !important;
    display: inline-block !important;
    margin-bottom: -16px !important;
    margin-left: 10px !important;
    position: relative !important;
    text-align: right !important;
    z-index: 100 !important;
color : gray  !important;
}

/* TEST - == "ADD ICONS (Flickr CARET) - IN BLOCK of TOP PAGE GOOGLE'S RESULTS === -   === */
#resultStats > a:before{
    background: url("http://l.yimg.com/g/images/flickr-sprite.png.v3") no-repeat scroll -1574px -93px transparent !important;
    color: transparent !important;
    content: "XX" !important;
    display: inline-block !important;
    height: 20px !important;
    margin-left: -10px !important;
    position: absolute !important;
    width: 10px !important;
    z-index: 100 !important;
}

/* IFRAME SHORT / LARGE - === */
 /* SHORT - 1338 === */
[id*="mydiv"][width="1338"] {
    background-color: white;
    display: block;
    left: 785px !important;
    max-height: 800px !important;
    position: fixed !important;
    top: 85px !important;
    width: 58% !important;
    z-index: 1024;
}
 /* LARGE - === */
[id*="mydiv"]{
    background-color: white;
    display: block !important;
    left: 405px !important;
    max-height: 800px !important;
   position: fixed !important;
    top: 85px !important;
    width: 78% !important;
    z-index: 1024 !important;
}

/* CLOSE BUTTON - === */
 
[id*="closeButton"] {
    background: none repeat scroll 0 0 red !important;
    border: 2px solid #C7D6F7 !important;
    border-radius: 5px 5px 5px 5px;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.3);
    height: 22px;
    left: 820px !important;
    position: fixed !important;
    text-decoration: blink !important;
    top: 93px !important;
}
[id*="closeButton"]:hover{
text-decoration:none !important;
}

/* SIZE BUTTON - === */

[id*="sizeButton"] {
    background-color: red !important;
    border: 2px solid #C7D6F7 !important;
    border-radius: 5px 5px 5px 5px;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.3);
    height: 22px;
    left: 795px !important;
    position: fixed !important;
    text-decoration: blink !important;
    top: 93px !important;
}
[id*="sizeButton"]:hover{
text-decoration:none !important;
}



/* ========== END ======= */

}
