#PopupAutoCompleteRichResult .autocomplete-richlistbox > scrollbox {
overflow:visible !important;}

#PopupAutoCompleteRichResult .autocomplete-richlistbox {
background: #f0f2ed !important;
margin: 1px !important;}

#PopupAutoCompleteRichResult .ac-site-icon {
margin: 0 8px 0 0 !important;
}

#PopupAutoCompleteRichResult .ac-title-box {
color: #444 !important;
}

#PopupAutoCompleteRichResult  .autocomplete-richlistbox .autocomplete-richlistitem  .ac-url-box{
margin: 0 !important;
padding: 0 0 1px 0 !important;
color: #ffe0ce !important;}

richlistitem.autocomplete-richlistitem {
border:1px solid transparent !important;
border-bottom-color: transparent !important;
box-shadow: none !important; padding:2px 4px !important;}

richlistitem.autocomplete-richlistitem:first-child {
border-top-color: transparent !important;
}

richlistitem.autocomplete-richlistitem[current] + richlistitem.autocomplete-richlistitem
{border-top-style:solid!important;}

richlistitem.autocomplete-richlistitem[type*="omnibar-suggestion"] {
background-color: rgba(0,0,0,.03) !important;} 

richlistitem.autocomplete-richlistitem[current] {
background: #f0774a !important}

#PopupAutoCompleteRichResult span.ac-emphasize-text  {
font-weight:normal !important;
background: hsla(60,91%,75%,.38);
text-decoration:none!important;
border-radius:2px !important;}
