style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("docs.google.com"), domain("spreadsheets.google.com") {
#logo-section{display:none !important ;} 
#searchForm {display:none !important ;}
#masthead {display:none !important ;}
#gbar {display:none !important ;}
#id_topnav{display:none !important ;}
#navigation > td > table{display:none !important ;}
}

@-moz-document url-prefix(http://www.google.com/reader) {
#gbar{display:none !important ;}
#global-info{display:none !important ;}
#logo-container{display:none !important ;}
#search{display:none !important ;}
#selectors-box{display:none !important ;}
}

@-moz-document domain(mail.google.com) {
	#gbh {
		display: none !important;
	}
	.gb2 {
		margin-top: -2px !important;
	}
	.fgrX7c > div > div > div > div > .IY0d9c { 
		margin-top: -12px !important;
	}
	.fgrX7c > div > div > div > div > .XoqCub { 
		margin: -92px 0px 0px 92% !important;
		overflow: hidden !important;
	}
	.fgrX7c > div > div > div > div > .XoqCub:hover { 
		margin: 0px !important;
		overflow: visible !important;
	}
	.n38jzf .cyVRte, .OVyDvc .cyVRte, .jU8Zgd .cyVRte {
		height: 12px !important;
		margin: -1px auto !important;
		position: absolute !important;
		left: 0px !important;
	}
}
