style.css:
@-moz-document url-prefix("http://www.google.com/calendar/"), url-prefix("https://www.google.com/calendar/") {

/******BETTER STYLES (white text etc)*****************************************************************************/
  
  .tg-today, .st-bg-today, .st-bg-td-first, .st-bg-td-last, .dp-today-selected { background-color: #FFF7D7 !important; }
  .rb-n { color: #333 !important; }
  .chip { color: #FFFFFF !important; }
  .rszr-icon-hover { border-color: #FFFFFF !important; }
  .tg-times-pri, .tg-times-sec {background-color: #F1F1F1 !important;}

  /*** white text for individual calendar items ***/
  dl.cbrd dd span {color:#fff !important;}
  dl.cbrd dt {color:#fff !important;}
  dl.cbrd:hover {background-color:#aaa !important;}


/******SLIM HEADER*****************************************************************************/
  #gbx3 {
    display: none;
  }
  #gb {
    display: none;
  }
  #vr-header {
    height: 32px !important;
  }
  #ntowner {
    height:32px !important;
  }
  #vr-nav {
    margin: 1px 0 0 !important;
    padding: 0 0 0 !important;
  }
  .domainlogoparent {
    top:0px !important;
  }
  #srreg{
    top:2px !important;
  }
  #mainbody {
    padding-top:1px !important;
  }
  .domainlogo img {
    width:70px !important;
  }
  div#nt2.mbox-cont {margin-top:25px !important;}
  

/******REMOVE SEARCH BAR*****************************************************************************/  
  #vr-header{display:none !important;}


  
}
