style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.bit-tech.net") {
.pageContainer {
   width: 100% !important;
}
.pageContainer .navContainer {
   background-position: center top !important;
   margin-top: -30px !important;
}
.pageContainer .navContainer + .navContainer {
   margin-top: 0px !important;
}
#brandHeader {
   height: 85px !important;
}
.grid_18 {
   width: 100% !important;
}
#column_1.grid_12 {
   width: auto !important;
   margin-right: 250px !important;
}
#column_2.grid_6 {
   position: absolute !important;
   top: 00px !important;
   right: 0px !important;
   width: 240px !important;
}
#column_1.grid_8 {
   width: auto !important;
   margin-right: 610px !important;
}
.doubleFeatureBlock {
   width: auto !important;
}
.grid_8 {
   width: auto !important;
}
ul.articles {
   -moz-column-count: 2;
   -webkit-column-count: 2;
}
.alpha.grid_8.omega + .alpha.grid_8.omega {
   float: none !important;
}
#column_1 .listBlock {
   clear: both !important;
   position: inherit !important;
   float: none !important;
}
#column_2 div {
   width: auto !important;
}
.articleCopy {
   -moz-column-count: 2;
   -webkit-column-count: 2;
}
#column_2.grid_4 {
   position: absolute !important;
   top: 0px !important;
   right: 300px !important;
   width: 300px !important;
}
#column_2 .listBlock ul {
   -moz-column-count: 2;
   -moz-column-gap: 0px;
   -webkit-column-count: 2;
   -webkit-column-gap: 0px;
}
#column_3.grid_6 {
   position: absolute !important;
   top: -10px !important;
   right: 0px !important;
}
#bodyContainer {
   padding-bottom: 200px !important;
}
}
