style.css:
@-moz-document domain("plus.google.com") {
@font-face {
    font-family: 'Textar';
    font-style: normal;
    font-weight: normal;
    src: local('MS PGothic'),local('Textar'),local('IPAMonaPGothic'),local('Mona'),
      url('https://dl.dropbox.com/u/520126/u/textar-min.woff') format('woff'),
      url('https://dl.dropbox.com/u/520126/u/textar-min.ttf') format('truetype'),
      url('https://dl.dropbox.com/u/520126/u/textar-min.svg#Textar') format('svg');
  }

  #contentPane [id^="update-"] .VC,
  #contentPane [id^="update-"] .Mi,
  #contentPane [id^="update-"] .Is,
  #contentPane [id^="update-"] .Ct,
  #contentPane .editable,
  #contentPane .editable > div,
  #notify-widget-pane [id^="update-"] .VC,
  #notify-widget-pane [id^="update-"] .Mi,
  #notify-widget-pane [id^="update-"] .Is,
  #notify-widget-pane [id^="update-"] .Ct,
  #notify-widget-pane .editable,
  #notify-widget-pane .editable > div,
  div[guidedhelpid="sharebox"] .VC,
  div[guidedhelpid="sharebox"] .Mi,
  div[guidedhelpid="sharebox"] .Is,
  div[guidedhelpid="sharebox"] .Ct,
  div[guidedhelpid="sharebox"] .editable,
  div[guidedhelpid="sharebox"] .editable > div {
    font-family: 'Textar', 'MS PGothic', 'IPAMonaPGothic', 'Mona', sans-serif !important;
    font-size: 11.5px !important;
    line-height: 1.125 !important;
    font: normal 11.5px/1.125 'Textar', 'MS PGothic', 'IPAMonaPGothic', 'Mona', sans-serif !important;
  }
}
