style.css:
/* Only today matters. This CSS removes the header, footer, someday, and all other days. It also makes today wider. — http://nieder.me */

@-moz-document domain('teuxdeux.com') {
	
	#content_wrapper {
		width: 520px !important;
		min-width: 500px !important;
	}
	
	#scrollContainer > div {
		padding-top: 30px !important;
	}
	
	#today {
		width: 500px !important;
		display: block !important;
	}
	
	.day_wrapper, #deux_list {
		border: none !important;
	}
	
	ul.list_items .done .teuxdeux {
		color: #ccc;
	}
	
	#header, #footer, #feedback_nib, div.done, .arrow, #someday {
		display: none !important;
	}
}
