/* 
 * @name 		Google Maps Minimizer
 * @website		http://userstyles.org/styles/61542
 * @description	Optimize the Google Mainbar for smaller Screens by resizing some Elements
 * @version		2018.05.17
 * @date 		2018.05.17
 */

@-moz-document 
  domain("mail.google.com")
{
    #gbq2 {
        padding-top: 5px !important;
    }
    #gba, .gb_Df, .gb_Pf, .gb_Qf, .gb_Rf, .gb_Sf {
        height: 40px !important;
    }
    .gb_Mb {
        padding: 1px 10px 0 10px !important;
    }
    .gb_Lb.gb_R.gb_Pb {
        max-width: 115px !important;
        min-width: 115px !important;
    }
    .gb_Df>.gb_R, .gb_Pf>.gb_R, .gb_Qf>.gb_R, .gb_Rf>.gb_R, .gb_Sf>.gb_R {
        height: 40px !important;
        line-height: normal !important;
    }
}
