style.css:
/* START - - - Apply to entire domain - - - START */
@-moz-document domain("forums.mozillazine.org"), domain("custombuttons.sourceforge.net")
{

#tooltip{
	padding: 3px!important;
	border: solid 1px #000000!important;
	display: none!important; /*white-space: nowrap!important;*/
	background: #FFF!important;
	color: #000000!important;
	font: 10px Verdana, Arial, Helvetica, sans-serif!important;
	text-align: left!important;
	position: absolute!important;
	left: 0!important;
	z-index: 1000!important;
	opacity: .85!important;
}

html, body, .content {color:#222!important}
#wrap {min-width:0!important;padding:0!important}
body {padding:0!important}

/* fonts/links */
.content, .content *, p, h2, h3, h4, h5, dt * {font-family:verdana,arial,sans!important}
h1, h2, h3 {color:#222!important;;font-weight:bold!important}
h2 {font-size:24px!important;line-height:1.4!important}
.postbody h3 {font-size:17px!important;}
.icon {text-transform:none!important}

/* phpbb navigation*/
.navbar {width:630px!important;float:left!important;clear:both!important}
.navbar .icon-home {padding:10px 0 5px!important}
.navbar li {display:inline!important;font-size:12px!important;line-height:1.5!important;font-weight:bold!important}
ul.linklist li {background-image:none!important}
ul.linklist li {font-size:12px!important;line-height:1.7!important}

/* phpbb board */
div.rules {padding:5px 10px!important;}
.forabg, .forumbg {padding:6px 3px 3px!important;padding-top:1px!important;background:transparent!important}

ul.topiclist dl.icon {padding:0!important}
li.header dl.icon dt {padding-right:61px!important;font-weight:bold!important}
li.header dd {margin:0 0px 0 0!important}
li.header dd.lastpost {margin-right:0!important;width:22%!important}
li.header dd.lastpost span {padding-left:10px!important}
dl.icon dd.lastpost span {float:left!important; margin-left:30px!important; width:80%!important;}

ul.topiclist dt {width:49%!important}
ul.topiclist li.row {padding:2px 0 0!important}
ul.topiclist li.row dt, ul.topics li.row dd {padding-top:5px!important;padding-bottom:15px!important}
dd.posts, dd.topics, dd.views {width:6%!important;}
dl.icon dd.views {float:left!important; margin-left:30px!important; width:6%!important;}
dl.icon[style*="unread"] img {width:20px!important; height:15px!important; background: none repeat scroll 0% 0% rgb(255,255,0)!important;}

ul.forums {background:#fff!important;}
ul.topics li.row {border-width:2px!important;}
ul.topics dt, ul.topics dd {font-size:10px!important;}

.post{padding:5px 0!important;}
.postbody {margin-right:5px!important;}
.postbody {float:right!important;border-left:1px solid #000000!important;width:78%!important;padding:10px 10px 10px 10px!important}
.postbody blockquote {background-image:none!important;}
.postprofile {border:none!important;width:19%!important;}
.search .postprofile {width:28%!important}
.search .postbody {width:69%!important}
.search.post {margin-top:25px!important;margin-bottom:0px!important;}
.postprofile img {border:1px solid #333!important; border-radius: 8px 8px 8px 8px!important;}
.postprofile dd, .postprofile dt {margin-left:4px!important;}
.posthilit {color:#000!important;padding:1px 4px!important;}
blockquote {margin:10px 1px 0 15px!important;}
blockquote.uncited {margin:10px 0 0 15px!important;padding:5px!important;}

/* paging */
.pagination span strong {background-color:yellow!important; border:transparent!important; color:black!important;text-shadow:none!important; font-weight:bold!important;}
.pagination span a, .pagination span a:link, .pagination span a:visited, .pagination span a:active {background-color:#eee!important;border-color:#aaa!important;color:#004776!important;}

/* buttons/icons/forms */
.sitehome, .icon-faq, .icon-members, .icon-home, .icon-ucp, .icon-register, .icon-logout, .icon-bookmark,
.icon-bump, a .icon-subscribe, .icon-unsubscribe, .icon-pages, .icon-search {background:none!important; padding:0px!important;}

select{padding:2px!important;}
fieldset.quickmod {width:auto!important;}
input#keywords.inputbox, input#search_keywords.inputbox, input#add_keywords.inputbox, input#author.inputbox {width:150px!important;padding:3px 3px 3px 17px!important;border-color:#666!important;background:#fff center left no-repeat ; font-size:11px!important}

input.button1, input.button2, input.button3, a.button1, a.button1:link, a.button1:visited, a.button1:active, a.button2, a.button2:link, 
a.button2:visited, a.button2:active {background:#fffeff no-repeat!important;color:#000!important; border:1px solid #666!important; padding:3px 6px 2px!important; font-weight:bold!important; font-size:10px!important; border-radius:8px!important;}

/*icon madness*/
ul.profile-icons {margin-right:auto!important}
li.web-icon {margin-right:auto!important; width:0px!important}
li.email-icon {margin-right:auto!important; width:0px!important}
ul.profile-icons li, ul.profile-icons li a {background-repeat:no-repeat!important;width:55px!important;height:20px!important}
ul.profile-icons li {float:right!important;margin:2px 2px 0!important}

.reply-icon, .post-icon, .pmreply-icon, .forwardpm-icon {width:96px!important;height:25px!important}
.locked-icon {width:84px!important;height:25px!important}
.newpm-icon {width:88px!important;height:25px!important}

a.right {font-size:11px!important;margin-right:10px!important;padding-right:14px!important}


/* adjust i15 */
.h2i15search_box div p {font-size:9px!important;}
.h2toplabel {padding:0 10px 4px 4px!important}
.h2weather p {margin:5px 0 0!important;/*ie6*/margin:0;font-size:10px!important}
.community .h2weather p {margin:0!important}
.community #content {padding:10px 0!important}
.community #copyright {margin:10px auto 100px!important}
#header p {margin-bottom:0!important}
.col1 {width:650px!important}
.col2 {float:right!important;width:300px!important;margin-right:10px!important}
.visit #header {height:150px!important}

/* default colors */
.navbar *, li.header dt, li.header dd {color:#010101!important}
.navbar a:link, .navbar a:visited, .navbar a:hover {color:#004776}
.navbar .rightside li a {border-right:1px solid #010101!important}
.headerbar h1 {color:#010101!important}
.navbar, .forumbg, .forabg {background-color:#D9D9D9!important}
li.row {border-color:#D9D9D9!important; border-radius:8px!important;}
.panel, .rules, .topics li:hover, .forums li:hover, .posthilit {background-color:#F0F0F0!important}
.topics li.reported:hover{background-color:#F0F0F0!important;}
ul.topiclist li.row dd, ul.topiclist li.row:hover dd{border-color:transparent!important}
.post {background-color:#F0F0F0}
.postbody blockquote {background:#fff!important;border:1px solid #ccc!important;}

.bg1:not(:hover), .bg2:not(:hover) {background:#f0f0f0!important}
ul.forums:not(:hover), li.row:not(:hover) {background:#fff!important}
ul.topics li.row {border-width:2px!important}
ul.topics dt, ul.topics dd {font-size:10px!important}

tr:hover, td#main, .navbar, span, tr, tr:hover, .forumbg, .forabg, DIV.panel.bg3, div.panel.bg2, .pm-panel-header, #topicreview, #topicreview .bg1, #topicreview .bg2, #preview.post.bg2, .pm-panel-message, .panel, h3, ul.topiclist.forums, #viewprofile *, #srEmoticons > DIV, #page-body > DIV[class="panel bg2"], #page-body > DIV[class="panel bg1"] {background-color:transparent!important; background:transparent!important; background-image:none!important; border:transparent!important;}

* .postbody {border-radius:8px!important; background:#EAECEC!important; border:2px solid #f0f0f0!important; padding:5px!important; margin-left:-8px!important;}

#topicreview {border-radius:8px!important; padding:5px!important; margin-right:5px!important;}

#main, .sidebar, .post, .panel, .forabg, #content, .navbar, blockquote, .bg1, .bg2, .reply-icon span, .post-icon span, .buttons, .button1, .button2, li.row, #tabs a, #navigation a, .cp-mini {border-radius:8px!important;}

div cite {background-color:#C1CFEF!important; font-size:1.05em!important; border:1px solid black!important; padding:5px!important;}

cite {border-radius: 8px 8px 0px 0px!important;}

blockquote {padding-bottom:5px!important; padding-right:0px!important; padding-left:0px!important; padding-top:0px!important;}

div.inner > span:not(.corners-top) , #sidebar img, FIELDSET.display-options.clearfix.bg1.pm-message-nav, #site-description, .postbody H3 > A, .corners-top {display:none!important;}

#postform .postbody {width:97%!important;}

#cp-main.ucp-main .postbody {background:#dcdcdc!important; border-radius:8px 8px 8px 8px!important;}

#nav_menu, div#yass_top_edge, #yass_top_edge_dummy, div#yass_top_edge {display:none!important; margin:0px!important;}

.header dt a[href*="50"], icon-home a, #page-body p:not(.author), p a:not(.author), #page-body h3:not(.postbody):not(.search):not(.first), .headerbar h1, #nytrmgwhoisonline a, #nytrmgwhoisonline p, #page-body h2 a:not(.postbody), #page-body h2:not(.postbody), * label, .right-box, .left-box, .pagination a, .pagination, .pagination strong, .navbar a, .icon-ucp, .icon-ucp strong, .rightside, .linklist li a, .linklist strong:not(:hover), .linklist li, fieldset a, fieldset .right, .display-actions a,.display-actions div, .header dl.icon dt, .header dl.icon dd.posts, .header dl.icon dd.views, .header dl.icon dd.lastpost, .header dl.icon dd.lastpost span, .header dl.icon dd.topics, #cp-main.ucp-main a.right-box.right, a.top2, A.left-box.left, h3 a:not(.postbody), UL.linklist.pm-return-to a, .ucp-main .pagination a:not(span), #cp-main.ucp-main .pagination, #cp-main li.header dt, h3#review, .pm-panel-header a, .pm-panel-header p, .locked-icon a, #options-panel label, #cp-main dd strong a, #cp-main h2, #cp-main .postprofile a, #cp-main .postprofile strong, #cp-main > DIV > DIV > DL > DD, #smiley-box, .reply-icon a, a.left, #options-quickreply a, div.buttons *, #viewprofile *:not(select), #postingbox a, .pagination * strong, tr.bg2:hover *, tr.bg1:hover *, ul.linklist * abbr, dl.details > dt, .pagination span a:hover, .fields2 dl dt span, .panel .inner dt span, form#register dl, dl.faq > dd > a:not(:hover), dl.faq > dt > strong, dl.faq > dd, #content > H1, #errorpage DIV, #errorpage a {color:white!important; text-shadow: -2px 0 2px black, 0 2px 2px black, 2px 0 2px black, 0 -2px 2px black!important;}

strong.pagination a, .pagination span a, .rightside.pagination span a, p.author, p.author strong a, p.author a, h3.first a, #cp-main h3.first, .ucp-main h3 a.current, .icon-ucp, .ucp-main .postbody h3 a, .postbody h3, input.button2, p.author a, #viewprofile .button2, dd.info span, #preview .postbody h3, #postform .postbody h3 a, .search h3 a {color:black!important; text-shadow:none!important;}

*:link:hover, * a:hover, a:hover:hover, a:active, a:link:hover, *.left:hover, *.left:active, A.icon-subscribe:hover, #page-body h2 a:hover:not(.postbody):hover, .buttons * a:hover, dl.icon > dt > a:not(.topictitle):not(.forumtitle):hover, .pmreply-icon.clearfix a:hover, dl.faq > dd > a:hover {color:black!important; text-shadow: -2px 0 2px white, 0 2px 2px white, 2px 0 2px white, 0 -2px 2px white!important; position:relative!important;-moz-transition-property:text-shadow, text-decoration, color!important; -moz-transition-duration:500ms!important; -moz-transition-timing-function:linear!important;}

.icon * a.forumtitle:hover, .icon * a:hover, a.postlink:hover:not(:visited), a.postlink-local:hover:not(:visited), .inner * a:hover, dl.icon > dt > a:hover, #preview .postbody h3:hover, #postform .postbody h3 a:hover, .search h3 a:hover  {color:inherit!important; text-shadow: -2px 0 2px #dcdcdc, 0 2px 2px #dcdcdc, 2px 0 2px #dcdcdc, 0 -2px 2px #dcdcdc!important; position:relative!important;-moz-transition-property:text-shadow, text-decoration, color!important; -moz-transition-duration:500ms!important; -moz-transition-timing-function:linear!important;}

#cp-main.ucp-main div.postbody {width:97.5%!important;}

ul.linklist {margin-top:10px!important;}

li.rightside {margin-top:10px!important;}

a img {width:18px!important; height:15px!important; display:inline!important;}

cite {margin-left:-5px!important;}

hr {border:none!important;}

html {padding-bottom:20px!important; background:#222222 url("http://i25.photobucket.com/albums/c70/MDLeers/pic_zps23b1877c.jpg") fixed no-repeat right top!important; background-size:cover!important;}

BODY#phpbb {margin-left:380px!important; margin-right:auto!important; }

div.content {margin-left:0px!important; margin-right:5px!important; }

blockquote {margin-left:5px!important; padding-left:5px!important; margin-right:auto!important; font-size:inherit!important;}

.friend-online a {color:green!important;}.friend-offline a {color:red!important;}

#login div.error {color:red!important; text-shadow: -2px 0 2px black, 0 2px 2px black, 2px 0 2px black, 0 -2px 2px black!important;}

blockquote div blockquote {width:90%!important;}

code {margin-left:auto!important; margin-right:auto!important; font-size:inherit!important;}

p.author {width:100%!important; margin-left:0px!important;  margin-right:auto!important; width:200px!important;}

div.post_body {width:100%!important; margin-left:0px!important; margin-right:auto!important;}

.postprofile {margin:0px 0px 0px 0px!important; padding:0pt!important; float:left!important;}

BODY.ltr {width:60%!important; margin-left:auto!important; margin-right:auto!important;}

div#wrap{width:85%!important;position:relative!important; left:30px!important; margin-top:-15px!important;}

stack browser {width:85%!important;}

DIV#acp {width: 50%!important; margin-left:auto!important; margin-right:auto!important;}

a.username-coloured {color:green!important; text-decoration:underline!important;}

BODY#phpbb.section-viewforum.ltr, BODY#phpbb.section-viewtopic.ltr, #errorpage, .ltr {background:transparent!important; padding:0px!important; position:relative!important; margin: 0 auto!important;}

a.postlink:not(:visited), #viewprofile .postbody div.signature a.postlink:not(:visited) {color:red !important; font-weight:bold !important; text-shadow:none!important;}

a.postlink-local:not(:visited), a.postlink[href*="forums.mozillazine"]:not(:visited) {color:green!important; border-bottom-color:green!important; font-weight:bold!important;}

a.postlink-local:visited, a.postlink:visited {color:#76787A!important; text-shadow: -2px 0 2px white, 0 2px 2px white, 2px 0 2px white, 0 -2px 2px white!important; text-decoration:none!important;}

a[href*="mailto"] {color:blue!important; border-bottom-color:green!important; font-weight:bold!important; text-decoration: underline!important;}

span#sprite, span.corners-bottom, h1 a img {display:none!important}

a.icon-subscribe:hover, a.icon-bookmark:hover, .icon-home strong a:hover {color:black!important; text-shadow: -2px 0 2px white, 0 2px 2px white, 2px 0 2px white, 0 -2px 2px white!important; position:relative!important;-moz-transition-property:text-shadow, text-decoration, color!important; -moz-transition-duration:500ms!important; -moz-transition-timing-function:linear!important;}

li[class*="row"]:not(:hover), .post.bg1:not(:hover), .post.bg2:not(:hover), .postbody:not(:hover), div.sidebar {background:rgba(245, 245, 245, 0.8)!important;}

li[class*="row"]:hover, .post.bg1:hover, .post.bg2:hover {background:rgba(245, 245, 245, 0.9)!important;}

li.row.bg1:not(:hover) {background:rgba(250, 240, 228, 0.8)!important;}

li.row.bg1:hover {background:rgba(250, 240, 228, 0.9)!important;}

}
