style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("kaskus.co.id") {
  .wrap.op1, .wrap.op2, .wrap.op3 {
    background-color: transparent !important;
    background-image: url("https://si0.twimg.com/errors/whale.png") !important;
    background-position: 50% 30%, 100% 100% !important;
    background-repeat: no-repeat, repeat-x !important;
    background-size: 283px 264px, 69px 43px !important;
  }
  .wrap.op1:before, .wrap.op2:before, .wrap.op3:before,
  .wrap.op1:after, .wrap.op2:after, .wrap.op3:after {
    content: "";
    position: fixed;
    top: 0; right: 0; bottom: 0; left: 0;
    pointer-events: none;
  }
  .wrap.op1:before, .wrap.op2:before, .wrap.op3:before {
    background-image: url("data:image/svg+xml;base64,");
    background-image:    -moz-radial-gradient(center, ellipse cover, #4786b3 0%, #2e5673 100%); /* FF3.6+ */
    background-image:      -o-radial-gradient(center, ellipse cover,  #4786b3 0%,#2e5673 100%); /* Opera 12+ */
    background-image:        -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,#4786b3), color-stop(100%,#2e5673)); /* Chrome,Safari4+ */
    background-image: -webkit-radial-gradient(center, ellipse cover,  #4786b3 0%,#2e5673 100%); /* Chrome10+,Safari5.1+ */
    background-image:         radial-gradient(center, ellipse cover,  #4786b3 0%,#2e5673 100%); /* W3C */
    z-index: -1;
  }
  .wrap.op1:after, .wrap.op2:after, .wrap.op3:after {
    background: url("https://si0.twimg.com/errors/whale_water.png")
                bottom left repeat-x;
    z-index: 3;
  }
  .wrap.op1 p, .wrap.op2 p, .wrap.op3 p { visibility: hidden; }
  .wrap.op1 p:before, .wrap.op2 p:before, .wrap.op3 p:before,
  .wrap.op1 p:after, .wrap.op2 p:after, .wrap.op3 p:after {
    text-indent: 0 !important;
    color: #fff;
    font-weight: bold !important;
    text-align: center !important;
    height: 0;
    position: absolute;
    left: 0; right: 0;
    display: block;
    visibility: visible;
  }
  .wrap.op1 p:before, .wrap.op2 p:before, .wrap.op3 p:before {
    content: "KASKUS is over posting.";
    bottom: 7.5em;
    font-size: xx-large !important;
  }
  .wrap.op1 p:after, .wrap.op2 p:after, .wrap.op3 p:after {
    content: "Please wait a moment and try again.";
    font-size: large !important;
    bottom: 11em;
  }
  .wrap.op3 h1 { visibility: visible !important; }
  .wrap.op1 a, .wrap.op2 h1 a, .wrap.op3 h1 a {
    top: 25px !important;
    left: 0 !important;
    right: 0 !important;
    z-index: 100;
    margin: auto !important;
    background: rgba(0, 0, 0, .25);
    padding: 1em 5em .5em !important;
  }
  .wrap.op1 + footer, .wrap.op2 + footer, .wrap.op3 + footer {
    display: none !important;
  }
}
