style.css:
@-moz-document domain("www.kongregate.com") {
/*
* UserStyles.org: moe.fm mine player (Style 61938)
*
* Author      : tumuyan
* Created     : Mar 24, 2012		
* Version     : 0.1.0 - release
*/

tumuyan{position:fixed;}
}

@-moz-document url-prefix("http://www.kongregate.com/games") {


body,.divider,.tabpane{
background:#666 !important;
}


.panel_body{
background:#fee !important;
border:2px solid #ddd;border-radius:5px
}


.panel_body:hover{
background:#ffe !important;
border:2px solid #eee;border-radius:5px
}


dl[class="description bd"] dt a,
#gamespotlight_container,
.game_details
{background:#999 !important;}


#belowgame_accomplishments
,input.hintable
,#game_discussion
{
background:#999 !important;
}

.user_message{background:#eee !important;}

h2>a,h5>a{}


#chat_default_content
,.disconnect_content
{background:#ccc !important;}


.game_accomplishment
{}

li.vtab a.active{ box-shadow: -2px 2px 2px #aaa !important;}

}
