style.css:
@-moz-document domain('plus.google.com') {
div.FF-L[role="dialog"] {
	/* 編集ダイアログの土台 */
	width	: 855px !important; 
	height	: 550px !important; 
}

div.FF-L[role="dialog"] > div:nth-child(2) > div > div > div:first-child > div > div:first-child {
	/* 書式設定バー */
	width	: 720px !important;
}

div.FF-L[role="dialog"] > div:nth-child(2) > div > div > div:first-child > div > iframe:nth-child(2) {
	/* 編集エリア */
	width	: 720px !important;
	height	: 420px !important;
}
} /* moz-documentブロック終 */
