style.css:
@namespace url(http://www.w3.org/1999/xhtml);

 @-moz-document
	domain(libranet.no-ip.org){

/* Felső reklám */
    .outer > A[target="_blank"]:first-child {display:none!important}

/* Név szélesség */
    .rounded > :first-child > TR > TD[align="left"][style^="border-style: none;"] {width:100px!important}

/* Név - Kép */
    TABLE[width="350"]:last-child {
         Width: 100px!important;
         position:relative!important;
         top:-7px!important; }

    IMG[height="100"] {height:150px!important;max-width:120px!important}
    
/* Név - Cím */ 
    .rounded [align="left"] > TABLE[width="100%"][border="0"]:first-child {
         position:absolute!important;
         width:700px!important;
         margin-left:125px!important;
         margin-top:12px!important; }


/* Elválasztók */

.colhead {text-align:center !important;}
.rounded , .rounded TR  { border: 1px solid rgba(0,0,0,.4)!important; }

.rounded TR TABLE TR    { border: 0px hidden!important;}

}
