style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(vk.com){

div.wall_text div.page_post_sized_thumbs {
max-height: 140px!important;
}

div.wall_text div.page_post_sized_thumbs a {
max-height: 140px !important;
}

div.wall_text div.page_post_sized_thumbs a img {
vertical-align: middle !important;
height: 100% !important;
width: auto !important;
}

/* видео */
.wide_wall_module .post_thumbs_num1 .page_media_video, .wall_module .page_media_full_video {
    max-height: 140px!important;
    max-width: 190px!important;
}
.wide_wall_module .post_thumbs_num1 .page_media_video .video img, .wall_module .page_media_full_video .video img {
    max-height: 140px!important;
    max-width: 190px!important;
}
}
