}
}


/* --------------- */
/* Preload images. */
/* --------------- */

div.header_after {
	width: 0px !important;
	height: 0px !important;
	display: inline !important;
	background-image: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_m_lb1.png) !important;
	background-image: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_m_records1.png) !important;
	background-image: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_m_find1.png) !important;
	background-image: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_m_driver1.png) !important;
	background-image: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_m_login1.png) !important;
	background-image: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_m_logout1.png) !important;
	background-image: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_m_lb2.png) !important;
	background-image: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_m_records2.png) !important;
	background-image: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_m_find2.png) !important;
	background-image: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_m_driver2.png) !important;
	background-image: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_m_login2.png) !important;
	background-image: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_m_logout2.png) !important;
}


/* --------------- */
/* Basic elements. */
/* --------------- */

body {
	font-family: Arial, Helvetica, sans-serif !important;
	color: #333 !important;
	/*[[wmdbg]]*/
	margin: 0 !important;
	padding: 0 !important;
}

form textarea {resize:none !important;}
a:focus { outline: none !important; /* Disable Firefox Dotted Line around Links */}

h1,
h2,
h3,
h4,
h5,
h6 {
	text-align: left !important;
	padding-left: 0.1em !important;
	text-transform: uppercase !important;
	font-family: 'Lato', Arial, Helvetica, sans-serif !important;
	font-weight: 900 !important;
	color: #FF6600 !important;
}

h1 {
	height: 119px !important;
	border-bottom: 1px solid #333 !important;
	padding: 0 30px !important;
	border-bottom: 1px solid #ddd !important;
	margin: 0 !important;
	font-size: 30px !important;
	line-height: 125px !important;
}

h1.page_title {
	line-height: normal !important;
}

h2 {
	background: #1c1b1d url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_sub.jpg) repeat-x !important;
	padding: 20px 60px !important;
	border: 0 !important;
	border-bottom: 2px solid #333 !important;
	margin: 0 !important;
	color: #FF6600 !important;
	text-transform: uppercase !important;
	font-family: 'Lato', Arial, Helvetica, sans-serif !important;
	font-weight: 400 !important;
}

h3,
h4,
h5,
h6 {
	border-bottom-width: 1px !important;
}

p,
p.help, 
div.login_text p, 
p.not_found, 
p.no_records,
div.error div.error_background,
div.fatal_error div.error_background {
	font-size: small !important;
	color: #999 !important;
	text-align: justify !important;
	margin: 0 !important;
	margin-top: 25px !important;
	padding: 20px 60px !important;
}

p.no_records {
	font-family: 'Lato', Arial, Helvetica, sans-serif !important;
	font-weight: 900 !important;
	font-size: 16px !important;
	text-transform: uppercase !important;
	color: #FF6600 !important;
}

p.footer {
	width: 920px !important;
	height: 100px !important;
	background: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_footer_logos.png) no-repeat right 19px !important;
	color: #4a4a4a !important;
	border: 0 !important;
	padding: 0 !important;
	margin: 0 !important;
	padding: 30px 0 0 30px !important;
	font-family: 'Lato', Arial, Helvetica, sans-serif !important;
	font-weight: 700 !important;
	font-size: 11px !important;
}

p.cache_age,
p.not_found, 
p.maintenance,
div.login p.login_error {
	background: #111 url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_alert.gif) no-repeat left top !important;
	color: #FF5500 !important;
	margin: 20px 60px !important;
	padding: 7px !important;
	padding-left: 40px !important;
	font-size: small !important;
	border: 1px solid #FF5500 !important;
}

p.not_found,
div.login p.login_error
{
	margin-top: 40px !important;  
}

p.cache_age {
	margin-bottom: 40px !important;
}

p.pager {
	text-align: right !important;
	padding: 4px 0.5em !important;
	font-size: small !important;
	border: none !important;
	margin: 0 !important;
}

tr.pager_top .pager {
	margin-bottom: 20px !important;
}

tr.pager_bottom .pager {
	margin-top: 20px !important;
}

a:link, 
a:visited, 
a:active { 
	text-decoration: none !important;
}

a:hover {
	text-decoration: underline !important;
	color: #FF6600 !important;
}

label {
	font-size: 12px !important;
	color: #333 !important;
}

input, 
textarea, 
select  {
	font-size: 12px !important;
	color: #444 !important;
	padding: 2px !important;
	border: 1px solid #ccc !important;
	-moz-appearance:  none  !important;
}

input:focus, 
textarea:focus, 
input:hover, 
textarea:hover, 
select {
	border: 1px solid #FF6600 !important;
}

td.button input, 
input.submit, 
span.direct input {
	font-size: 12px !important;
	font-family: 'Lato', Arial, Helvetica, sans-serif !important;
	font-weight: 900 !important;
	color: #FFF !important;
	text-transform: uppercase !important;
	background: #333 !important;
	border: 0 !important;
	padding: 5px 20px !important;
	border-left: 4px solid #FF6600 !important;
}

td.button input:hover, 
input.submit:hover, 
span.direct input:hover {
	color: #FFF !important;
	background: #FF6600 !important;
	border-color: #444 !important;
	cursor: pointer;
}


/* --------------- */
/* Error messages. */
/* --------------- */

div.error {
	padding: 0 !important;
}

div.fatal_error {
	margin-top: 1em !important;
	border: 0 !important;
	padding: 5px !important;
}

div.error div.error_background,
div.fatal_error div.error_background {
	background: transparent !important;
}

div.error p.error,
div.fatal_error p.error {
	background-color: inherit !important;
	font-family: 'Lato', Arial, Helvetica, sans-serif !important;
	font-weight: 900 !important;
	font-size: 16px !important;
	text-transform: uppercase !important;
	color: #FF6600 !important;
	padding: 10px !important;
	border-bottom: 0 !important;
}

div.error p.help,
div.fatal_error p.help {
	padding-left: 10px !important;
	padding-right: 10px !important;
	padding-bottom: 10px !important;
}


/* --------- */
/* Top menu. */
/* --------- */

div.header {
	margin: 0 !important;
	padding: 0 !important;
	position: fixed !important;
	top: 0 !important;
	left: 0 !important;
	width: 100% !important;
	min-width: 980px !important;
	margin: 0 !important;
	padding: 0 !important;
	background: #000 repeat-x !important;
	border-top: 5px solid #FF6600 !important;
	border-bottom: 1px solid #333 !important;
	-webkit-box-shadow: 0 6px 6px -6px black !important;
	   -moz-box-shadow: 0 6px 6px -6px black !important;
	        box-shadow: 0 6px 6px -6px black !important;
}

div.header_menu {
	width: 100% !important;
	height: 75px !important;
}

ul.header_menu {
	float: right !important;
	color: #ccc !important;
	margin: 0 !important;
	padding: 0 !important;
	list-style-type: none !important;
	overflow: hidden !important;
	background: transparent !important; 
}

ul.header_menu li {
	display: inline !important;
	margin: 0 !important;
}

ul.header_menu li a:link, 
ul.header_menu li a:visited, 
ul.header_menu li a:active {
	float: left !important;
	display: block !important;
	text-decoration: none !important;
	margin: 0 !important;
	padding: 6px 15px !important;
	border: 0 !important;
	color: #91979C !important;
	font-family: 'Lato', Arial, Helvetica, sans-serif !important;
	font-weight: 700 !important;
	font-size: 12px !important;
	text-transform: uppercase !important;
	height: 60px !important; 
	line-height: 63px !important;
	background: transparent !important;
	border-bottom: 3px solid transparent !important;
}


ul.header_menu li.active_page a {
	color: #FFFFFF !important;
	border-bottom: 3px solid #FFF !important;
}

ul.header_menu li a:hover {
	border-bottom: 3px solid #FF6600 !important;
	color: #FF6600 !important;
}

ul.header_menu li.leaderboards a {
        padding-left: 51px !important;
        background: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_m_lb0.png) left no-repeat !important;
}

ul.header_menu li.global_records a {
        padding-left: 53px !important;
        background: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_m_records0.png) left no-repeat !important;
}

ul.header_menu li.find_user a {
        padding-left: 53px !important;

        background: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_m_find0.png) left no-repeat !important;
}

ul.header_menu li.my_profile a {
        padding-left: 51px !important;
        background: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_m_driver0.png) left no-repeat !important;
}

ul.header_menu li.login a {
        padding-left: 54px !important;
        background: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_m_login0.png) left no-repeat !important;
}

ul.header_menu li.logout a {
        padding-left: 53px !important;
        background: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_m_logout0.png) left no-repeat !important;
}


/* ------------------ */
/* Page in general. */
/* ------------------ */

.page_title {
	color: #999 !important;
	font-weight: normal !important;
	text-transform: uppercase !important;
}

span.game_name {
	display: block !important;
	font-family: 'Lato', Arial, Helvetica, sans-serif !important;
	font-weight: 400 !important;
	font-size: 11px !important;
	line-height: 6px !important;
	padding-top: 36px !important;
	color: #BBB !important;
}

span.page_title {
	font-family: 'Lato', Arial, Helvetica, sans-serif !important;
	font-weight: 900 !important;
	color: #FF6600 !important;
}

span.dash {
	display: none !important;
  	color: #999 !important;
	font-weight: normal !important;
}

span.page_title span.user_name {
	color: #444 !important;
	text-transform: none !important;
	font-weight: normal !important;
}

div.global_records,
div.find_user,
div.profile,
div.login,
div.error,
div.fatal_error,
div.leaderboard_container,
#content {
	-webkit-box-shadow: 0 0px 6px 0px black !important;
	   -moz-box-shadow: 0 0px 6px 0px black !important;
	        box-shadow: 0 0px 6px 0px black !important;
	width: 980px !important;
	margin: 220px auto 20px auto !important;
	padding: 0 !important;
	padding-bottom: 50px !important;
	border: 0 !important;

}

div.select_leaderboard {
	background: #222 !important;
	padding: 10px 0 !important;
}

div.footer {
	width: 980px !important;
	height: 136px !important;
	margin: 0 auto  !important;
	background: #111 url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_footer_bg.gif) repeat-x !important;
	border-top: 1px solid #444 !important;
	padding: 0 !important;
}

p.not_found span.game_name, p.not_found span.user_name {
	display: inline !important;
	font-size: 13px !important;
	line-height: normal !important;
	padding-top: 0 !important;
	color: #FFF !important;
	font-weight: bold !important;
}

p.not_found span.user_name {
	text-transform: uppercase !important;
}


/* ------------------ */
/* Tables in general. */
/* ------------------ */

table {
	border: 0 !important;
	border-collapse: collapse !important;
	margin: auto !important;
	width: 860px !important;
}

table th,
table td {
	border: none !important;
}

table tr.header {
	background: #FF6600 !important;
	font-family: 'Lato', Arial, Helvetica, sans-serif !important;
	font-weight: 900 !important;
	color: #000 !important;
	text-transform: uppercase !important;
	height: 40px !important;
	font-size: 12px !important;
	border-bottom: 2px solid #000 !important;
}

table tr.sub_header {
	background: #999 !important;
	color: #FFFFFF !important;
}

table tr {
	vertical-align: top !important;
}

table th {
	text-align: left !important;
}

table td,
table th {
	vertical-align: middle !important;
}

table tr td,
table tr th {
	padding: 3px 10px !important;
}

table tr.header td,
table tr.header th,
table tr.sub_header td,
table tr.sub_header th {
	padding: 5px 10px !important;
	font-weight: 900 !important;
}

table tr.no_data {
	background: #AAAAAA !important;
	color: #000000 !important;
}

table tr.no_data td.no_data {
	text-align: center !important;
	font-weight: bold !important;
}

table tr.row_1,
table tr.row_2 {
	background: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_tdrow_shad.png) top repeat-x !important;
}

table tr.row_1 {
	background-color: #e6e6e6 !important;
	color: #333 !important;
}

table tr.row_2 {
	background-color: #FFF !important;
	color: #333 !important;
}

table tr.row_top_1,
table tr.row_top_2,
table tr.row_top_3,
table tr.row_1.current_user, 
table tr.row_2.current_user {
	background: url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_tdrow_glow.png) bottom repeat-x !important;
}

table tr.row_top_1 {
	background-color: #d5c36f !important;
	color: #333 !important;
}

table tr.row_top_2 {
	background-color: #c4cdd6 !important;
	color: #333 !important;
}

table tr.row_top_3 {
	background-color: #BC9A74 !important;
	color: #333 !important;
}

table tr.row_1.current_user, table tr.row_2.current_user {
	background-color: #6BCAE9 !important;
	color: #333 !important;
}

table tr th.button {
	text-align: center !important;
}

td label, span.next a, span.first a, span.previous a {
	font-family: 'Lato', Arial, Helvetica, sans-serif !important;
	font-weight: 700 !important;
	font-size: 12px !important;
	color: #FFF !important;
	text-transform: uppercase !important;
}

span.next a, span.first a, span.previous a {
	padding: 0 5px !important;
}

span.next a:hover, span.first a:hover, span.previous a:hover  {
	color: #FF6600 !important;
}

span.vehicle_overall_best, span.vehicle_name {
	color: #FFF !important;
	font-weight: 300 !important;
}

span.track_name {
	color: #FF6600 !important;
	margin: 5px !important;
}

h2.leaderboard_title span.page_title {
	display: block !important;
	padding-left: 5px !important;
	font-size: 12px !important;
	color: #555 !important;
	font-weight: 700 !important;
	line-height: 14px !important;
}


/* ------------ */
/* Log in page. */
/* ------------ */

table.login_form input.string,
table.login_form input.password {
	width: 20em !important;
}


/* --------------- */
/* Find user page. */
/* --------------- */

table.find_user_form input.string {
	width: 20em !important;
}

table.user_list th.user,
table.user_list td.user {
	text-align: left !important;
	min-width: 20em !important;
}

table.user_list td.user a {
	background-color: inherit !important;
	color: #000000 !important;
}

table.user_list td.user a:active {
	background-color: inherit !important;
	color: #FF6600 !important;
}


/* ------------- */
/* Leaderboards. */
/* ------------- */

table.select_leaderboard,
table.select_leaderboard th,
table.select_leaderboard td {
	border: none !important;
}

table.leaderboard_container,
table.leaderboard_container > tbody > tr > th,
table.leaderboard_container > tbody > tr > td {
	border: none !important;
	padding: 0 !important;
}

table.leaderboard th.rank {
	text-align: center !important;
}

table.leaderboard td.rank_icon {
	text-align: center !important;
	padding-right: 5px !important;
}

table.leaderboard td.rank {
	text-align: right !important;
	padding-left: 5px !important;
}

table.leaderboard th.user,
table.leaderboard td.user {
	text-align: left !important;
	min-width: 20em !important;
}

table.leaderboard th.vehicle,
table.leaderboard td.vehicle {
	text-align: left !important;
	min-width: 20em !important;
}

table.leaderboard th.time,
table.leaderboard td.time {
	text-align: center !important;
	min-width: 5em !important;
}

table.leaderboard th.gap,
table.leaderboard td.gap {
	text-align: center !important;
	min-width: 5em !important;
}

table.leaderboard td.user a,
table.leaderboard td.vehicle a {
	background-color: inherit !important;
	color: #222 !important;
}

table.leaderboard td.user a:active,
table.leaderboard td.vehicle a:active {
	background-color: inherit !important;
	color: #222 !important;
}


/* -------- */
/* Profile. */
/* -------- */

div.profile_image {
	width: 100px !important;
	height: 100px !important;
	margin: 8px 15px 0 8px !important;
	float: left !important;
	background: #222 url(http://i1189.photobucket.com/albums/z429/brafxs/pCARS%20Leaderboard/wmd_lb_driverimg.jpg) no-repeat !important;
	border: 2px solid #CCCCCC !important;
	text-align: center !important;
}

div.profile_image img {
	width: 100px !important;
	height: 100px !important;
}


/* ---------------------- */
/* Profile records table. */
/* ---------------------- */

table.profile_records th.leaderboard_name,
table.profile_records td.leaderboard_name {
	text-align: left !important;
	min-width: 20em !important;
}

table.profile_records th.rank {
	text-align: center !important;
}

table.profile_records td.rank_icon {
	text-align: center !important;
	padding-right: 5px !important;
}

table.profile_records td.rank {
	text-align: right !important;
	padding-left: 5px !important;
}

table.profile_records th.time,
table.profile_records td.time {
	text-align: center !important;
	min-width: 5em !important;
}

table.profile_records th.gap,
table.profile_records td.gap {
	text-align: center !important;
	min-width: 5em !important;
}

table.profile_records tr.sub_header td.leaderboard_name a {
	background-color: inherit !important;
	color: #FFFFFF !important;
}

table.profile_records td.leaderboard_name a {
	background-color: inherit !important;
	color: #222 !important;
}

table.profile_records td.leaderboard_name a:active,
table.profile_records td.leaderboard_name a.track:active {
	background-color: inherit !important;
	color: #222 !important;
}


/* --------------------- */
/* Global records table. */
/* --------------------- */

table.global_records th.user,
table.global_records td.user {
	text-align: left !important;
	min-width: 20em !important;
}

table.global_records th.vehicle,
table.global_records td.vehicle {
	text-align: left !important;
	min-width: 20em !important;
}

table.global_records th.time,
table.global_records td.time {
	text-align: center !important;
	min-width: 5em !important;
}

table.global_records tr.sub_header th.track a {
	background-color: inherit !important;
	color: #FFFFFF !important;
}

table.global_records td.user a,
table.global_records td.vehicle a {
	background-color: inherit !important;
	color: #222 !important;
}

table.global_records tr.sub_header th.track a:active,
table.global_records td.user a:active,
table.global_records td.vehicle a:active {
	background-color: inherit !important;
