.tab_notice_nipple:before {



  position: absolute !important;



  content: "" !important;



  width: 0 !important;



  height: 0 !important;



  left: 0 !important;



  border-top: solid 4px #6d95b5 !important; 



  border-right: solid 4px transparent !important;



  border-bottom: solid 3px transparent !important;



  border-left: solid 3px #6d95b5 !important; 



  z-index: 2 !important;



}



.tab_notice_nipple:after {



  background-image: none !important;



  position: absolute !important;



  content: "" !important;



  width: 0 !important;



  height: 0 !important;



  top: .25px !important;



  left: 1px !important;



  border-top: solid 3.75px #6d95b5 !important; 



  border-right: solid 3.75px transparent !important;



  border-bottom: solid 3.75px transparent !important;



  border-left: solid 3.75px #6d95b5 !important; 



  z-index: 1 !important;







}



