style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(slashdot.org) {
  #links {
    position: fixed !important;
    margin-top: 1em !important;
  }

  #links > div[id^="block"] > .content {
    position: absolute !important;
    z-index: 32767 !important;
    margin-top: -1.9em !important;
    margin-left: 9em !important;
    width: 10em !important;
    height: 0 !important;
    border-width: 0 1px 1px 0 !important;
    border-style: solid !important;
    border-color: #666 !important;
    background-color: #eee !important;
    display: none !important;
  }

  #links > div[id^="block"]:hover > .content {
    display: block !important;
    height: auto !important;
  }

  #links > div[id^="block"] h4 {
    padding-left: 1em !important;
    background-image: url(http://images.slashdot.org/block-arrow-expanded.gif) !important;
    background-position: 8.25em 50% !important;
  }

  #links > div[id^="block"]:hover h4 {
    background-image: url(http://images.slashdot.org/block-arrow-collapsed.gif) !important;
    background-position: 8.5em 50% !important;
  }
}
