style.css:
@-moz-document domain("mail.google.com") {
body.aAU > div:nth-child(2) { top: -30px; }
}

@-moz-document url-prefix("https://www.google.com/calendar/"), url-prefix("http://www.google.com/calendar/") {
body { margin-top:-30px; }
}

@-moz-document url-prefix("http://www.google.com/reader"), url-prefix("http://www.google.co.uk/reader"), domain("maps.google.co.uk") , domain("maps.google.com"), domain("drive.google.com") {
div#gb { top: -30px; position: relative; width: 100%; }
div#main, div#page, div#doclist { padding: 0px 0 0 0; margin: 0px; top: -30px; }
#gbq.gbes, #gbu.gbes { padding: 20px; }
}
