.notification_type_icon.follow_icon {
  background-image: none !important;
}
.notification_type_icon.follow_icon:before {
  position: absolute !important;
  content:"" !important;
  background: rgba(255,255,255,1) !important;
  left:4px !important;
  width: 14px !important;
  height: 5px !important;
  margin-top: 8px !important;
  margin-left: -4px !important;
  border-top-right-radius: 4px !important;
  border-top-left-radius: 4px !important;      
}
.notification_type_icon.follow_icon:after {
  position: absolute !important;
  content:"" !important;
  background: rgba(255,255,255,1) !important;
  left: 9px !important;
  width: 6px !important;
  height: 10px !important;
  margin-top: 0px !important;
  margin-left: -5px !important;
  border-radius:5px 5px 5px 5px / 5px 5px 10px 10px !important;
  -webkit-border-radius:5px 5px 5px 5px / 5px 5px 10px 10px !important;
  border-radius:5px 5px 5px 5px / 5px 5px 10px 10px;
}

.notification_type_icon.like_icon {
  background-image: none !important;
}

.notification_type_icon.like_icon:before,
.notification_type_icon.like_icon:after {
  position: absolute !important;
  content: "" !important;
  left: 10px !important;
  top: 0px !important;
  width: 10px !important;
  height: 15px !important;
  background: rgba(0,0,0,1) !important;
  border-radius: 10px 10px 0 0 !important;
  -webkit-border-radius: 50px 50px 0 0 !important;
  -moz-transform: rotate(-45deg) !important;
  -webkit-transform: rotate(-45deg) !important;
  -moz-transform-origin: 0 100% !important;
  -webkit-transform-origin: 0 100% !important;
}
.notification_type_icon.like_icon:after {
  left: 0px !important;
  background: rgba(0,0,0,1) !important;
  -moz-transform: rotate(45deg) !important;
  -webkit-transform: rotate(45deg) !important;
  -moz-transform-origin: 100% 100% !important;
  -webkit-transform-origin: 100% 100% !important;
}
.notification_type_icon.like_icon:before,
.notification_type_icon.like_icon:after {
  margin-top: -1px !important;
  margin-left: -5px !important;
}

.notification_type_icon.reblog_icon {
  margin-top: 2px !important;
  background-image: none !important;
  border-top: solid 2px rgba(255,255,255,.75) !important;
  border-right: solid 2px rgba(255,255,255,.5) !important;
  border-bottom: solid 2px rgba(255,255,255,.75) !important;
  border-left: solid 2px rgba(255,255,255,.5) !important;
  border-bottom-right-radius: 1px !important;
  border-top-left-radius: 1px !important;
  -webkit-border-bottom-right-radius: 1px !important;
  -webkit-border-top-left-radius: 1px !important;
  border-bottom-right-radius: 1px !important;
  border-top-left-radius: 1px !important;

  width: 10px !important;
  height: 5px !important;
}
.notification_type_icon.reblog_icon:before {
  position: absolute !important;
  content:"" !important;
  border-top: solid 4px transparent !important;
  border-bottom: solid 4px transparent !important;
  border-left: solid 4px rgba(255,255,255,1) !important;
  left: 0px !important;
  width:0px !important;
  height: 0px !important;
  margin-top: -5px !important;
  margin-left: 10px !important; 
}
.notification_type_icon.reblog_icon:after {
  position: absolute !important;
  content:"" !important;
  border-top: solid 4px transparent !important;
  border-right: solid 4px rgba(255,255,255,1) !important;
  border-bottom: solid 4px transparent !important;
  left: 0px !important;
  width:0px !important;
  height: 0px !important;
  margin-top: 2px !important;
  margin-left: -4px !important;
}


