style.css:
@-moz-document domain(rsr.ch) {

#portal-navigation { display:none; }

#selections,
#visual-portal-wrapper {margin-right:20px !important;}

#portal-columns,
#portal-menu,
#channel-wrapper,
#portal-column-wrapper{width:100% !important;}

#selections,
#portal-column-content{width:auto !important;}

.portlet-programm-cadre,
.portlet-emissionAZ-cadre{
 height:auto !important;
 width:auto !important;
 overflow:visible !important;
 color:#f00;}

#selections .selection { position:relative !important; }
#selections .selection .selection-text {
 left:170px !important;
 position:absolute !important;
 top:0 !important;
 width:auto !important; }

#selections .selection + .selection .selection-text,
#selections .selection + h4 + .selection .selection-text {
 left:95px !important; }

}
