@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitpic.com") {

/* muahah too easy - removes all the ads */
iframe { display:none !important; }

/* "Advertise on Twitpic" */
a[href*="advertise.twitpic.com"] { display:none !important; }

}
