style.css:
@-moz-document url-prefix('https://www.facebook.com/') {

/*隱藏右側的廣告區*/
div.ego_section {
    display: none !important;
}

/*背景圖片設定*/
body.hasLeftCol.home.composerExpanded.fbx.hasSlimHeader.ff4.win.Locale_zh_TW {
    background-image: url(/*[[mybackgroundimage]]*/) !important;
    background-attachment: fixed !important;
    background-repeat: no-repeat !important;
}

/*留言區域透明化*/
li.uiUfiComment.ufiItem.ufiItem {
    background: rgba(255,255,255,0) !important;
    border-style: none !important;
}

/*左方padding增加*/
div.linkWrap.noCount {
    padding-bottom: 9px !important;
}

/*左欄區域設定*/
#leftCol {
    background: rgba(255,255,255,0.6) !important;
    margin-left: -20px !important;
}

/*收件匣訊息展開，"其他"-padding設定*/
a.subitem.clearfix div {
    padding-bottom: 2px !important;
}
/*收件匣訊息-padding設定*/
a.item.clearfix.sortableItem div {
    padding-bottom: 2px !important;
}

/*中+右方區塊下移、背景透明*/
#contentCol {
    background: rgba(255,255,255,0) !important;
    margin-top: 30px !important;
    border-style: none !important;
}

/*右方內部區塊設定*/
div.rightColumnWrapper {
    background: rgba(255,255,255,0.6) !important;
    padding-bottom: 6px !important;
}

/*收件匣訊息-主畫面邊框&字色設定*/
div.UIImageBlock.clearfix.pvs {
    color: #000000 !important;
    border-style: none !important;
}

/*訊息串(中間)設定*/
#contentArea {
    background: rgba(255,255,255,0) !important;
    width: 650px !important;
}

/*每則訊息的訊息區設定*/
div.storyInnerContent.UIImageBlock_Content.UIImageBlock_MED_Content {
    background: rgba(255,255,255,0.65) !important;
    padding-left: 10px !important;
    -webkit-border-radius: 10px !important;
    -webkit-box-shadow: 8px 8px 30px rgba(0,0,0,0.8) !important;
}

/*右側欄位位置調整*/
#rightCol {
    margin-right: -150px !important;
    background: rgba(255,255,255,0) !important;;
}

/*訊息區的大頭照*/
img.uiProfilePhoto.profilePic.uiProfilePhotoLarge.img.hoverZoomLink {
    box-shadow:4px 4px 4px rgba(0,0,0,0.8) !important;
}

/*右側即時訊息*/
div.tickerToggleWrapper.displayedTickerToggleWrapper.fbRemindersThickline {
    display: none !important;
}

/*訊息區塊，用來調整顯示訊息的間距*/
div.UIImageBlock.clearfix {
    border-style: none !important;
}

/*收件匣頁面訊息背景區設定*/
li.threadRow.noDraft.uiListItem.uiListLight.uiListVerticalItemBorder {
    border-style: none !important;
    background: rgba(255,255,255,0.6) !important;
}

/*點選訊息，進入訊息歷史頁面，上方的浮動欄位區域*/
div.MessagingReadHeader {
    background: rgba(255,255,255,0) !important;
}

/*歷史訊息的訊息區域*/
div.content.noh {
    background: rgba(255,255,255,0.6) !important;
}

/*社團內部修改邊框*/
div.groupJumpLayout.homeWiderContent.homeFixedLayout.homeFixedLayout.hasRightCol div {
    border-style: none !important;
}

/*PO文選項背景設定*/
ul.uiList.uiListHorizontal.clearfix.uiComposerAttachments {
    background: rgba(255,255,255,0.6) !important;
}

/*社團內部之上方寬度修正*/
#headerArea {
    width: 910px !important;
}

/*社團內部之右側訊息*/
div.groupsAddMemberSideBox {
    background: rgba(255,255,255,1) !important;
    padding: 1px !important;
}

/*訊息串-訊息區-間距設定*/
div.storyContent {
    margin-top: 25px !important;
    margin-bottom: 25px !important;
}

/*尋找朋友-下方列出朋友-區塊背景(每單位顯示人物-去掉第一位)*/
li.pvm.friendBrowserListUnit.uiListItem.uiListLight.uiListVerticalItemBorder {
    background: rgba(255,255,255,0.6) !important;
}
/*尋找朋友-下方列出朋友-區塊背景(每單位顯示人物-第一位)*/
li.pbm.friendBrowserListUnit.uiListItem.uiListLight.uiListVerticalItemBorder {
    background: rgba(255,255,255,0.6) !important;
}

/*尋找朋友-說明*/
div.mvs.mls.uiP.fsm {
    background: rgba(255,255,255,1) !important;
}

/*尋找朋友-標題*/
h2.uiHeaderTitle {
    background: rgba(255,255,255,1) !important;
}

/*每則訊息下方-留言後方區塊設定*/
li.uiUfiAddComment.clearfix.uiUfiSmall.ufiItem.ufiItem.uiListItem.uiListVerticalItemBorder.uiUfiAddCommentCollapsed {
    background: rgba(0,0,0,0) !important;
}

/*摯友-右方欄位設定*/
div.phs {
    background: rgba(255,255,255,0.6) !important;;
}

table.uiGrid.userSuggestionRow {
    border-style: none !important;;
}

div.friendListTypeaheadBox {
    background: rgba(255,255,255,1) !important;;
}

/*朋友-名單頁面區*/
a.pvm.phs.itemLink {
    background: rgba(255,255,255,0.6) !important;;
}


/*以下為邊框消去部分(並非全部)*/
div.homeFixedLayout.homeWiderContent.hasExpandedComposer.newsFeedComposer.composerOpen.homeFixedLayout.hasRightCol div {
    border-style: none !important;
}

#pagelet_reminders div {
    border-style: none !important;
}

li.showAll.uiStreamShowAll.pts.uiListItem.uiListLight.uiListVerticalItemBorder {
    border-style: none !important;
}



div.homeFixedLayout.homeWiderContent.hasExpandedComposer.newsFeedComposer.composerOpen.homeFixedLayout.hasRightCol {
    border-style: none !important;
}

li.seeAllItem.clearfix.uiListItem.uiListLight.uiListVerticalItemBorder {
    border-style: none !important;
}

#mainContainer {
    border-style: none !important;
}

.uiListLight {
    border-style: none !important;
}

li.mts.uiStreamHeader {
    display: none !important;
}

div.rhcFooterBorder {
    display: none !important;
}

div.uiStreamEdgeStoryLine hr {
    display: none !important;
}
/*以上為邊框消去部分*/


/*以下區塊的位置我忘記了=口=*/

/*某處的文字顏色*/
strong.attachmentName {
    color: #000000 !important;
}

div.pvm.uiBoxWhite.noborder {
    background: rgba(255,255,255,0.6) !important;
}

div.ptm.pbl.fsm.fwn.fcg {
    background: rgba(255,255,255,0.6) !important;
}

li.objectListItem.uiListItem.uiListLight.uiListVerticalItemBorder {
    background: rgba(255,255,255,0.6) !important;
}

}
