style.css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("matome.naver.jp") {


/* contents page -------------------------------------------------*/
.MdPagination03 {
    font-size: 20px !important;
}

.MdPagination03 a:link, .MdPagination03 a:visited {
    border: none !important;
    color: #5AB647 !important;
}

.MdPagination03 strong {
    color: black !important;
    padding: 8px 11px !important;
}

.MdPagination03 a:hover, .MdPagination03 a:active, .MdPagination03 a:focus {
    color: white !important;
}

div.MdPagination03 > span.mdPagination04Break {
    color: DimGrey !important;
    vertical-align: middle;
}


/* TOP page -------------------------------------------------*/
.MdPagination04 {
    font-size: 20px !important;
}

.MdPagination04 a:link, .MdPagination03 a:visited {
    border: none !important;
    color: #5AB647 !important;
}

.MdPagination04 strong {
    color: black !important;
    padding: 6px 8px !important;
}


.MdPagination04 a:hover, .MdPagination04 a:active, .MdPagination04 a:focus {
    color: white!important;
}

div.MdPagination04 > span.mdPagination04Break {
    color: DimGrey !important;
    display: inline;
}

/*-------------------------------------------------*/


}
