style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("craigslist.org") {
  body { font-family: Arial; }
  table { text-align: center; width:100%;}
  table.city { letter-spacing: 1px !important; }
  table.city b { letter-spacing: 2px !important; }
  div#logo { letter-spacing: 1.5px; }
  td#leftbar div { letter-spacing: 1.5px !important; }
  i:before { content: ".......... [ "; }
  i:after { content: " ]"; }
  p:before { content: " - "; }
}
