style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("furaffinity.net")
{

/*
* 
*     "Coded" by Unfiltered @  www.furaffinity.net/user/unfiltered
*/

body > div {
    padding-top: 45px !important;
}

/* TOPBAR TWEAKS */

.block-menu-top a { color: #fff !important; }

.block-menu-top { /* Sticky top menu */
    top: 0px !important;
    position: Fixed;
    -moz-box-shadow: 0px 0px 10px #000;
    -webkit-box-shadow: 0px 0px 10px #000;
    box-shadow: 0px 0px 10px #000;
    background-color: #292929;
    border-bottom: 1px inset #212121;
    opacity: 0.9; /* remove or change value to change transparency */
    z-index: 500 !important;
}

ul.dropdown .noblock [href*="controls"] { /* removes usless information on the menu */
    display: none !important;
}

#my-username, ul.dropdown .noblock [title*=Notes] { /* color change on Username and notes */ 
    color: #aaff00 !important;
    text-transform: capitalize;
}

#logout-link { /* color change on lougout button */
    color: #fff !important;
    margin-left: -12px;
    margin-right: -5px;
}

#logout-link:hover { /* color change on hover lougout button */
    color: #FF4444 !important;
}

}
