style.css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

@-moz-document url(chrome://browser/content/browser.xul), url(about:addons), url(about:preferences) {
	/********* back/forward buttons like safari *********/
	#navigator-toolbox[iconsize="small"][mode="icons"] > #nav-bar #back-button,
	#navigator-toolbox[iconsize="small"][mode="icons"] > #nav-bar #forward-button,
	#back-btn, #forward-btn {
		min-width:      25px !important;
		max-width:      25px !important;
	}
	#navigator-toolbox[iconsize="small"][mode="icons"] > #nav-bar #back-button > .toolbarbutton-icon,
	#back-btn > .toolbarbutton-icon {
		margin-right: -1px;
	}
	#navigator-toolbox[iconsize="small"][mode="icons"] > #nav-bar #forward-button > .toolbarbutton-icon,
	#forward-btn > .toolbarbutton-icon {
		margin-left: -1px;
	}
}
